Create Function RCTG.LU_REQDAYSONHOLD (pRequisitionid integer, pDate DATE)

	Returns INTEGER 
	Specific LU_REQDAYSONHOLD 
	Language SQL 
	Deterministic  
	No External Action 
	Reads SQL Data     
	Begin atomic
   declare v_statusdate DATE;--
   declare v_daysonhold INTEGER default 0;--
   declare v_Return INTEGER default 0;--
   set v_statusdate = pDate;--
   FOR c_rec AS
     SELECT date(r.StatusDate) as statusdate,
      r.Status as status
	   FROM STAGE_RCTG.REQSTATUS r 
	   WHERE r.requisitionid = pRequisitionid 
	     AND date(r.statusdate) <= pDate
	   ORDER BY r.statusdate DESC
   DO
      IF status = 'Hold'
      THEN
         set v_daysonhold = v_daysonhold + (days(date(v_statusdate)) - days(date(statusdate)));--
      END IF;--
      set v_statusdate = date(statusdate); --
   END FOR;--
   RETURN v_daysonhold;--
END;


