Create Function RIDQADM.LOCTIME (GMT_TIME TIMESTAMP, USA_ZONE VARCHAR(50))

	Returns TIMESTAMP  
	Language SQL 
	Not Deterministic  
	No External Action 
	Contains SQL     
		BEGIN ATOMIC 
DECLARE OUTPUT_DATE, LOCAL_TIME TIMESTAMP; --
DECLARE CURRENT_YEAR INTEGER ; --
DECLARE LAST_OF_MARCH CHAR(10); --
DECLARE FAD CHAR(10); --
DECLARE LOD CHAR(10); --
DECLARE FIRST_SUN_APRIL TIMESTAMP; --
DECLARE LAST_OF_OCT CHAR(10); --
DECLARE FIRST_SUN_APRIL_DT DATE; --
DECLARE FIRST_SUN_OCT_DT DATE; --
DECLARE LAST_SUN_OCT TIMESTAMP; --
DECLARE SEC_SUN_MAR TIMESTAMP;--
DECLARE FIRST_SUN_NOV TIMESTAMP;--
DECLARE FIRST_OF_MARCH CHAR(10);--
DECLARE FIRST_OF_NOV CHAR(10);--
DECLARE SMD CHAR(10);--
DECLARE FND CHAR(10);--

IF USA_ZONE = 'NT' THEN 
	SET LOCAL_TIME=(GMT_TIME - 3 HOURS - 30 MINUTES) ; --
ELSEIF USA_ZONE = 'AT' THEN 
	SET LOCAL_TIME=(GMT_TIME - 4 HOURS) ; --
ELSEIF USA_ZONE='ET' THEN 
	SET LOCAL_TIME=(GMT_TIME - 5 HOURS); --
ELSEIF USA_ZONE = 'CT' THEN 
	SET LOCAL_TIME=(GMT_TIME - 6 HOURS) ; --
ELSEIF USA_ZONE = 'MT' THEN 
	SET LOCAL_TIME=(GMT_TIME - 7 HOURS) ; --
ELSEIF USA_ZONE ='PT' THEN 
	SET LOCAL_TIME=(GMT_TIME - 8 HOURS ); --
END IF ; --

SET CURRENT_YEAR = YEAR(GMT_TIME); --
SET LAST_OF_MARCH = RTRIM(CHAR(YEAR(GMT_TIME))) || '-03-31'; --
SET FAD = char(next_Day(DATE(LAST_OF_MARCH), 'SUNDAY')); --
SET FIRST_SUN_APRIL = TIMESTAMP(FAD || ' 02:00:00'); --
SET LAST_OF_OCT = '10-31-' || RTRIM(CHAR(YEAR(GMT_TIME))); --
SET LOD = char(next_Day((DATE(LAST_OF_OCT) - 7 DAYS), 'SUNDAY')); --
SET LAST_SUN_OCT = TIMESTAMP(LOD || ' 02:00:00'); --

SET FIRST_OF_MARCH = RTRIM(char(DATE(RTRIM(CHAR(YEAR(GMT_TIME))) || '-03-01') - 1 DAYS)) ;--
SET SMD = char(next_day(next_day(DATE(FIRST_OF_MARCH), 'SUNDAY'),'SUNDAY'));--
SET FND = char(next_day(DATE(LAST_OF_OCT), 'SUNDAY'));--
SET SEC_SUN_MAR = TIMESTAMP(SMD || ' 02:00:00');--
SET FIRST_SUN_NOV = TIMESTAMP(FND || ' 02:00:00');--

IF USA_ZONE = 'HST' THEN 
	SET LOCAL_TIME=(GMT_TIME - 10 HOURS); --
ELSEIF CURRENT_YEAR < 2007 AND LOCAL_TIME >= FIRST_SUN_APRIL AND LOCAL_TIME < LAST_SUN_OCT THEN 
	SET LOCAL_TIME = LOCAL_TIME + 1 HOUR; --
ELSEIF CURRENT_YEAR > 2006 AND LOCAL_TIME >= SEC_SUN_MAR AND LOCAL_TIME < FIRST_SUN_NOV THEN
	SET LOCAL_TIME = LOCAL_TIME + 1 HOUR;--
END IF; --

IF USA_ZONE is null THEN 
	SET LOCAL_TIME=GMT_TIME; --
END IF; --

SET OUTPUT_DATE = LOCAL_TIME; --

RETURN OUTPUT_DATE; END;


