                            where 
                                 dpsg2.mi_dept_grouping = dpsg1.mi_dept_grouping and
                                  dpsg2.mi_dept_subgroup = dpsg1.mi_dept_subgroup and
                                  effdt <= a.job_bid_date)
             fetch first row only)          ;--
             
   UPDATE RCTG.CR133_CURR_JOB a
      SET (SPEC_DESCR) = 
          (SELECT   dpspc1.descr
            FROM    stage_rctg.ps_mi_dpt_speclty dpspc1
           WHERE    
                    dpspc1.mi_dept_grouping = a.mi_dept_grouping and
                   dpspc1.mi_dept_subgroup = a.mi_dept_subgroup and
                    dpspc1.mi_dept_specialty = a.mi_dept_specialty and
                    effdt = (select max(effdt) from stage_rctg.ps_mi_dpt_speclty dpspc2 
                              where 
                                   dpspc2.mi_dept_grouping = dpspc1.mi_dept_grouping and
                                    dpspc2.mi_dept_subgroup = dpspc1.mi_dept_subgroup and
                                    dpspc2.mi_dept_specialty = dpspc1.mi_dept_specialty and
                                    effdt <= a.job_bid_date)
            fetch first row only);                                --
            
   UPDATE RCTG.CR133_CURR_JOB a
      SET (SSPEC_DESCR) = 
          (select dpspc1.descr 
             from stage_rctg.ps_mi_dpt_sbspclty dpspc1
           WHERE 
                 dpspc1.mi_dept_grouping = a.mi_dept_grouping and
                dpspc1.mi_dept_subgroup = a.mi_dept_subgroup and
                dpspc1.mi_dept_specialty = a.mi_dept_specialty and
                 dpspc1.mi_dept_sbspeclty = a.mi_dept_sbspeclty and 
                 dpspc1.effdt = (select max(effdt) from stage_rctg.ps_mi_dpt_sbspclty dpspc2
                                  where 
                                        dpspc2.mi_dept_grouping = dpspc1.mi_dept_grouping and
                                        dpspc2.mi_dept_subgroup = dpspc1.mi_dept_subgroup and
                                        dpspc2.mi_dept_specialty = dpspc1.mi_dept_specialty and
