IF p_Event = 'Main' then
   
   DELETE FROM RCTG.CR133_MAIN;--
   
   
   SELECT BEG_DT, END_DT INTO V_BEG_DT, V_END_DT
   FROM RCTG.CR133_PARM
   WHERE ID = (SELECT max(id) from RCTG.CR133_PARM);--
   
   
   
   INSERT INTO RCTG.CR133_MAIN
   select distinct t1.cand_ky, t1.rqtn_ky
     from rctg.mqt_recr_Act_fct t1,
          rctg.whs_cand_dim t2
    where t1.cand_ky = t2.cand_ky
      and t1.cand_ky is not null
      and t1.rqtn_ky is not null
          
      and ( (t1.jobbid_stat in ('External - Consider Further',
                                'Internal - Eligibility Verified',
                                'Internal - Exception Verified',
                                'External - Remove From Consideration'))
    OR
      
            (t1.app_tp = 'External' and t2.src_sys_id = 'U')
          )
   INTERSECT
   
   select distinct cand_ky, rqtn_ky
    from rctg.whs_recr_act_fct
   where date(recr_act_dt) between V_BEG_DT and V_END_DT
   and cand_ky is not null
   and rqtn_ky is not null;--
   
   INSERT INTO RCTG.CR133_MAIN
   select d1.CAND_KY, d1.RQTN_KY 
   from rctg.mqt_recr_Act_fct d1
   where d1.RQTN_KY in (select distinct d2.RQTN_KY from RCTG.CR133_MAIN d2)
