CREATE PROCEDURE RCTG.POP_DATE_DIM
 (IN PPARM1 CHARACTER(4), 
  IN PPARM2 INTEGER
 ) 
  
  LANGUAGE SQL
  NOT DETERMINISTIC
  CALLED ON NULL INPUT
  MODIFIES SQL DATA
  INHERIT SPECIAL REGISTERS
  --------------------------------------------------
-- Create procedure RCTG.POP_DATE_DIM
--------------------------------------------------
Begin

DECLARE v_BeginDate DATE;--
DECLARE v_Days INTEGER;--
DECLARE v_counter INTEGER DEFAULT 1;--

-- Delete all rows before inserting new rows.
DELETE FROM RCTG.WHS_DT_DIM;--

-- Insert row for NULL date.  Dates will default to '1900-01-01'
INSERT INTO RCTG.WHS_DT_DIM (dt, full_dt_ds) values (DATE('1900-01-01'),'Unspecified Date');--

SET v_BeginDate = DATE(pParm1 || '-01-01');--
SET v_Days		= pParm2 * 365;--

WHILE (v_counter < v_Days) DO

	INSERT INTO RCTG.WHS_DT_DIM (dt, full_dt_ds, cal_dd_nm, cal_mm_nm, cal_ddomm, cal_yy,
	                        wkdy_ind, cal_wk_nbr_yy, cal_mm_nbr_yy, cal_ddoyy, 
	                        fdow_ind, ldow_ind, fdom_ind, ldom_ind, cal_wk_endt, 
	                        cal_yy_mm, cal_qq, cal_yy_qq, cal_fdoq_ind, cal_ldoq_ind, 
	                        pr_wk_bgdt, pr_wk_endt, curr_yy_bgdt, curr_yy_endt)
	SELECT v_BeginDate dt,
	       dayname(v_BeginDate) || ', ' || monthname(v_BeginDate) || ' ' ||
	       rtrim(char(day(v_BeginDate))) || ', ' || char(year(v_BeginDate)) full_dt_ds,
	       dayname(v_BeginDate) cal_dd_nm,
	       monthname(v_BeginDate) cal_mm_nm,
	       day(v_BeginDate) cal_ddomm,
	       rtrim(char(year(v_BeginDate))) cal_yy,
	       case when dayofweek(v_BeginDate) in (1,7)
	            then 'Weekend'
	            else 'Weekday' end wkdy_ind,
	       week(v_BeginDate) cal_wk_nbr_yy,
	       month(v_BeginDate) cal_mm_nbr_yy,
	       dayofyear(v_BeginDate) cal_ddoyy,
	       case when dayofweek(v_BeginDate) = 1
	            then 'Yes'
	            else 'No'
	            end fdow_ind,
	       case when dayofweek(v_BeginDate) = 7
	            then 'Yes'
	            else 'No'
	            end ldow_ind,
	       case when day(v_BeginDate) = 1
	            then 'Yes'
	            else 'No'
	            end fdom_ind,
	       case when day(v_BeginDate + 1 day) = 1
	            then 'Yes'
	            else 'No'
	            end ldom_ind,
	       v_BeginDate + (7 - dayofweek(v_BeginDate)) days cal_wk_endt,
	       case when month(v_BeginDate) < 10
	            then rtrim(char(year(v_BeginDate))) || '-0' || rtrim(char(month(v_BeginDate)))
	            else rtrim(char(year(v_BeginDate))) || '-'  || rtrim(char(month(v_BeginDate)))
	            end as cal_yy_mm,
	       'Q' || rtrim(char(quarter(v_BeginDate))) cal_qq,
	       rtrim(char(year(v_BeginDate))) || 'Q' || rtrim(char(quarter(v_BeginDate))) cal_yy_qq,
	       case when month(v_BeginDate) in (1,4,7,10) and day(v_BeginDate) = 1
	            then 'Yes'
	            else 'No'
	            end as cal_fdoq_ind,	            
	       case when month(v_BeginDate + 1 day ) in (1,4,7,10) and day(v_BeginDate + 1 day) = 1
	            then 'Yes'
	            else 'No'
	            end as cal_ldoq_ind,
           v_BeginDate + 1 day - dayofweek(v_BeginDate) days -7 days pr_wk_bgdt,
           v_BeginDate - dayofweek(v_BeginDate) days pr_wk_endt,
	       v_BeginDate - dayofyear(v_BeginDate) days + 1 day curr_yy_bgdt,
	       v_BeginDate - dayofyear(v_BeginDate) days + 1 year curr_yy_endt
	       from sysibm.sysdummy1;--
	       
	       SET v_counter = v_counter + 1;--
	       SET v_BeginDate = v_BeginDate + 1 day;--
	       
	       END WHILE;--
	       
	       -- Update Marriott Periods
	       UPDATE RCTG.WHS_DT_DIM T1
	       SET FSCL_PD = (SELECT left(char(FISCAL_YR),4) || 'P' || 
	                      case
	                      when t2.period < 10 then left('0'||char(T2.PERIOD),2) 
	                      else left(char(T2.PERIOD),2) 
	                      end as pd	                      
	                      from STAGE_RCTG.PERIOD_CAL T2
	                       WHERE T1.DT BETWEEN T2.CAL_BGDT and T2.CAL_ENDT),
	           FSCL_PD_QQ = (SELECT left(char(T2.FISCAL_YR),4) || 'PQ' || 
	                         case 
	                         when t2.period < 4 then '1'
	                         when t2.period < 7 then '2'
	                         when t2.period < 10 then '3'
	                         when t2.period < 14 then '4'
	                         end as qq
	                         from STAGE_RCTG.PERIOD_CAL T2
	                         WHERE T1.DT BETWEEN T2.CAL_BGDT and T2.CAL_ENDT);--
	                 
	        -- Update Marriott Prior Period and Prior Period Quarter Begin and End Dates        
			UPDATE RCTG.WHS_DT_DIM T1
			SET PR_FSCL_PD_BGDT = 
			       (SELECT MIN(DT)
			          FROM RCTG.WHS_DT_DIM T2
			         WHERE T2.FSCL_PD  = 
			               (SELECT MAX(FSCL_PD)
			                  FROM RCTG.WHS_DT_DIM T4
			                 WHERE T4.FSCL_PD < T1.FSCL_PD)),
			    PR_FSCL_PD_ENDT = 
			       (SELECT MAX(DT)
			          FROM RCTG.WHS_DT_DIM T3
			         WHERE T3.FSCL_PD  < T1.FSCL_PD),
			    PR_FSCL_PQ_BGDT = 
			       (SELECT MIN(DT)
			          FROM RCTG.WHS_DT_DIM T2
			         WHERE T2.FSCL_PD_QQ  = 
			               (SELECT MAX(FSCL_PD_QQ)
			                  FROM RCTG.WHS_DT_DIM T4
			                 WHERE T4.FSCL_PD_QQ < T1.FSCL_PD_QQ)),
			   PR_FSCL_PQ_ENDT = 
			       (SELECT MAX(DT)
			          FROM RCTG.WHS_DT_DIM T3
			         WHERE T3.FSCL_PD_QQ  < T1.FSCL_PD_QQ);--
			         
End ;

SET SYSIBM.NLS_STRING_UNITS = 'SYSTEM';
