Create Procedure RCTG.SP_MC_ALL_SLA_HTTS (IN Start_Date DATE, IN End_Date
DATE) 
 Specific SP_MC_ALL_SLA_HTTS 
 Dynamic Result Sets 0 
 Reads SQL Data     
 Language SQL 
Begin
 CURSOR1:BEGIN
        
        DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
select Req#,
           "External Posting Date",
           case when char(coalesce("Candidate Name",'NULL')) = 'NULL' then 'SLA Not Met'
                   else char("Time to Source") end as "Time to Source",
            "Total Positions",
            "SLA Count Requirement",
            "Candidate Name",
            "Application ID",
            "Candidate Type",
            "Application Date",
            "Region",
            HOLD_BEGIN_DATE as "Hold Begin Date",
            HOLD_END_DATE as "Hold End Date",
            org_lvl_nm_1 as "Business Unit",
            rqtn_curr_stat as "Req Current Status",
            org_lvl_nm_5 as "Location",
            prim_hr_rep_nm as "HR Rep",
            "Business Unit Code",
            "Location Code",
            "Hire Date",
            "Job Function",
            "Job Title",
            "State",
            "Req Open Date",
            "Business Days Open"
from (            
select rqtn_nr as Req#,
          date(rqtn_aprv_dt) as "External Posting Date",
/*          case when char(coalesce(date(b.event_date),'NULL')) = 'NULL' then 0
                  when coalesce(date(b.event_date),'1900-01-01') < date(a.rqtn_aprv_dt) then 0
      else ridqadm.bus_days(date(a.rqtn_aprv_dt),date(b.event_date)) end as "Time to Source",*/
    ridqadm.bus_days(date(a.rqtn_aprv_dt),date(b.event_date)) as "Time to Source",
          rqtn_ttl_pstn_ct as "Total Positions",
          rqtn_ttl_pstn_ct*3 as "SLA Count Requirement",
          cand_nm as "Candidate Name",
          cand_id as "Application ID",
          cand_tp as "Candidate Type",
          date(event_date) "Application Date",
          org_lvl_nm_2 as "Region",
          HOLD_BEGIN_DATE,
          HOLD_END_DATE,
          org_lvl_nm_1,
          org_lvl_nm_5,
          rqtn_curr_stat,
          prim_hr_rep_nm,
          org_lvl_id_1 as "Business Unit Code",
          org_lvl_id_5 as "Location Code",
          psu.effdt as "Hire Date",
          job_fn as "Job Function",
          bus_ttl as "Job Title",
          state as "State",
          date(a.rqtn_aprv_dt) "Req Open Date",
          case when rqtn_curr_stat <> 'Filled' then
          			ridqadm.bus_days(date(a.rqtn_aprv_dt),date(rqtn_curr_stat_dt))
          	   else
          			ridqadm.bus_days(date(a.rqtn_aprv_dt),current date -1 day)
          end  as "Business Days Open"
 from 
(
select * from (
select rqtn_nr, rqtn_ky, rqtn_aprv_dt, rqtn_ttl_pstn_ct, rqtn_curr_stat, org_lvl_nm_2, org_lvl_nm_1,org_lvl_nm_5, prim_hr_rep_nm,
    org_lvl_id_1, org_lvl_id_5, ridqadm.add_bus_days(date(rqtn_aprv_dt),6) as filter_dt, job_fn, bus_ttl, r1.state,
   (select min(date(rqtn_stat_bgdt)) from rctg.whs_rqtn_stat t1, rctg.whs_rqtn_stat_dim t2
     where t1.rqtn_stat_ky = t2.rqtn_stat_ky and t2.rqtn_stat_nm = 'Hold'
      and r1.rqtn_ky = t1.rqtn_ky) as HOLD_BEGIN_DATE,
   (select min(date(rqtn_stat_endt)) from rctg.whs_rqtn_stat t1, rctg.whs_rqtn_stat_dim t2
     where t1.rqtn_stat_ky = t2.rqtn_stat_ky and t2.rqtn_stat_nm = 'Hold'
      and r1.rqtn_ky = t1.rqtn_ky) as HOLD_END_DATE,
    rqtn_curr_stat_dt
   from rctg.whs_rqtn_dim r1,
            stage_rctg.requisition r2,
            rctg.whs_org_dim org
   where r1.rqtn_id = r2.requisitionid
   and r2.postingtype = 'E'
   and tp_of_opng = 'Hourly/Non-Management'
   and rctg_ctr = 'Hewitt'
   and r1.org_unit_key = org.org_unit_key
   and r1.rqtn_curr_stat not in ('Canceled', 'Pending Approval')) t1
--   and ridqadm.add_bus_days(date(rqtn_aprv_dt),6) between ? and ?) a
   where filter_dt between Start_Date and End_Date
   ) a
left outer join    
(SELECT   WHS2.CAND_NM, WHS2.CAND_ID, WHS2.CAND_TP, whs.rqtn_ky, min(recr_act_dt) event_date
FROM     RCTG.WHS_RECR_ACT_FCT WHS, 
         RCTG.WHS_RECR_EVNT_DIM WHS1 ,
         RCTG.WHS_CAND_DIM WHS2
WHERE    WHS.RECR_EVNT_KY=WHS1.RECR_EVNT_KY 
AND WHS.CAND_KY = WHS2.CAND_KY
AND WHS.SRC_SYS_ID = 'U'
AND WHS1.RECR_EVNT_NM = 'Test' AND WHS.RECR_ACT_RSLT_1 in ('Green','Clear')
AND 0 = (select count(*) from stage_rctg.unc_std_workflow w where unc_apl_id = WHS2.CAND_ID and end_wkf_stat_cd = 'HRR')
 group by whs2.cand_nm, WHS2.CAND_ID, whs2.cand_tp, whs.rqtn_ky
) as b
on a.rqtn_ky = b.rqtn_ky
left outer join STAGE_RCTG.SP_PSOFT_UPDATE PSU on b.CAND_ID = PSU.CANDIDATE_ID AND CHAR(a.RQTN_NR) = PSU.REQUISITION_NBR
  ) t1
  order by 1,6
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

