  date(off_aprv_dt) "Offer Approved Date", 
  coalesce(char(date(off_ltr_dt)),'n/a') "Offer Letter Date",
  /* Number of business days between Offer Approved Date and Offer Letter Date */
   case when coalesce(char(off_ltr_dt),'NULL') = 'NULL' then 'Offer Letter not generated via Deploy'
           when coalesce(date(off_ltr_dt),'1900-01-01') < date(off_aprv_dt) then char(-1)
   else char(ridqadm.bus_days(date(off_aprv_dt),date(off_ltr_dt))) end as "Days to Offer Letter",
   region,
   status as "Req Current Status",
   location,
   recruiter,
   workgroup_string as "Workgroup",
   "Location Code",
   "Business Unit Name",
   "Business Unit",
   jobcode as "Req Occ Code"
from 
(
/* Determine Offer Letter Date - max date of correspondence offer letter */
select t5.*,sc.lastname, sc.firstname, r.requisitionnumber, t6.off_ltr_dt, r.org_lvl_nm_2 region, r.org_lvl_nm_5 location, r.status, 
r.prim_recr_nm recruiter, workgroup_string, r."Location Code", r."Business Unit Name", r."Business Unit", r.jobcode
from 
stage_rctg.offer off
inner join (
/* Determine Offer Approved Date - last modified date of offerapprover with status like Approved where there are no other statuses */
select offerid, 
           syslastmodifiedtime off_aprv_dt,
           (select count(distinct substr(status,1,8)) from stage_rctg.offerapprover t2
              where t2.offerid = t1.offerid) ct
 from stage_rctg.offerapprover t1
 where exists (select 'x' from stage_rctg.offerapprover t3
                         where t3.offerid = t1.offerid
                           and substr(status,1,8) = 'Approved')
) t5 on off.offerid = t5.offerid
inner join stage_rctg.summ_cand sc on off.candidateid = sc.candidateid 
inner join (select requisitionid, requisitionnumber, org_lvl_nm_2, org_lvl_nm_5, status, prim_recr_nm, workgroup_string,
   ORG.ORG_LVL_ID_5 "Location Code", ORG.ORG_LVL_NM_1 "Business Unit Name", ORG.ORG_LVL_ID_1 "Business Unit", rqtn.jobcode
                       from stage_rctg.requisition r, 
                                rctg.whs_rqtn_dim rqtn,
                                rctg.whs_org_dim org 
                    where r.ismanagement = 'M' 
                        and r.recruitingcenter = 'HEW'
                        and r.status not in ( 'Cancelled', 'Pending Approval')
                        and r.requisitionid = rqtn.rqtn_id
                        and substr(rqtn.jobcode,1,6) not in ('332260','958020','959101','959201','959301','959401','959501','959601','959199','959299','959399',
                        									'959499','959100','959939','959949','959959','967240','967210','959124','959800','959810','959820',
                        									'959830','959840','959850','959860','959870','959880','959890','959900','959910','959920','959102',
                        									'959940','959302','959919','959950','959930','771990')
                        and rqtn.org_unit_key = org.org_unit_key) r on off.requisitionid = r.requisitionid
left outer join (select c.candidateid, c.requisitionid, max(statusdate)  off_ltr_dt
 from stage_rctg.correspondence c 
     where c.correspondenceevent = 'offer' 
       and   c.status = 'generated'
       group by candidateid, requisitionid) t6 on t6.candidateid = decimal(off.candidateid) and t6.requisitionid = off.requisitionid
where t5.ct = 1                           
and off.offerstatus in ('Accepted','Hired')
) t99
) t1
where "Offer Approved Date" between Start_Date and End_Date
order by 4, 3, 1, 2
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

