         WHS.REF_CHK_RSLTS as"Ref Chk Results",
         coalesce((select distinct 'Y' from stage_rctg.requisitionsource rs where rs.requisitionid = whs3.rqtn_id and sourcetype = '16'),'N') as "Source Internal?",
         coalesce((select distinct 'Y' from stage_rctg.requisitionsource rs where rs.requisitionid = whs3.rqtn_id and sourcetype = '19' and (days(enddate) - days(begindate)>=2)),'N') as "Source External for 2 days?",
         (select org_lvl_id_1 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Business Unit",
         (select org_lvl_nm_1 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Business Unit Name",
