--         rqtn_curr_stat as "Req Current Status",
         coalesce((select distinct 'Y' 
            from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED 
            where WHS.RQTN_KY = RAF.RQTN_KY 
            AND RAF.CAND_KY = WHS.CAND_KY
            and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY 
            AND RED.RECR_EVNT_NM = 'Test' 
            and RECR_EVNT_TP like 'DDI%' 
            and recr_act_cmpl_dt is not null),'N') as "DDI Assessment Taken"         ,
            -- Replace this section with the new section at the end
         (select recr_evnt_tp
            from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED 
            where WHS.RQTN_KY = RAF.RQTN_KY 
            AND RAF.CAND_KY = WHS.CAND_KY
            and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY 
            AND RED.RECR_EVNT_NM = 'Test' 
            and RECR_EVNT_TP like 'DDI%' 
            and recr_act_cmpl_dt is not null
    and recr_act_ky = (select max(recr_act_ky) 
          from rctg.whs_recr_act_fct raf1,
             rctg.whs_recr_evnt_dim red1
         where raf1.recr_evnt_ky = red1.recr_evnt_ky
           and red1.recr_evnt_tp like 'DDI%'
           and raf1.rqtn_ky = raf.rqtn_ky
           and raf1.cand_ky = raf.cand_ky
           and raf1.recr_act_cmpl_dt is not null)) as "DDI Assessment Type"         ,
         (select recr_act_rslt_1
            from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED 
            where WHS.RQTN_KY = RAF.RQTN_KY 
            AND RAF.CAND_KY = WHS.CAND_KY
            and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY 
            AND RED.RECR_EVNT_NM = 'Test' 
            and RECR_EVNT_TP like 'DDI%' 
            and recr_act_cmpl_dt is not null
    and recr_act_ky = (select max(recr_act_ky) 
          from rctg.whs_recr_act_fct raf1,
             rctg.whs_recr_evnt_dim red1
         where raf1.recr_evnt_ky = red1.recr_evnt_ky
           and red1.recr_evnt_tp like 'DDI%'
           and raf1.rqtn_ky = raf.rqtn_ky
           and raf1.cand_ky = raf.cand_ky
           and raf1.recr_act_cmpl_dt is not null)) as "DDI Assessment Result"         ,
         WHS2.CAND_CURR_LOC_DS as "Current Location",
         WHS2.CURR_JOB_FN_NM as "Current Job Function",
         WHS2.CAND_CURR_OCC_CD as "Current OCC Code",
         WHS3.workgroup_string as "Workgroup",
         date(WHS3.RQTN_APRV_DT) as "Req Open Date",
         WHS3.JOBCODE as "Req OCC Code",
         -- Use this section to replace the DDI Assessment Type section
         (select descr from 
         (select jobcode, setid, MI_ASSESSMENT 
            from stage_Rctg.SP_JOBCODE_TBL jc
           where effdt = (select max(effdt) from stage_Rctg.sp_jobcode_tbl jc1
                           where jc1.setid = jc.setid
                             and jc1.jobcode = jc.jobcode)) t2,
         (select * from stage_rctg.sp_code_descr_tbl
           where FIELDNAME = 'MI_ASSESSMENT'
             and LANGUAGE_CD = 'ENG') t3
         where t2.mi_assessment = t3.fieldvalue
           and substr(whs3.jobcode,1,6) = t2.jobcode
           and '000' || substr(whs3.jobcode,8,2) = t2.setid) as "DDI Assessment Type New"

FROM     RCTG.MQT_RECR_ACT_FCT WHS,
         RCTG.WHS_CAND_DIM WHS2,
         RCTG.WHS_RQTN_DIM WHS3
WHERE    WHS.CAND_KY=WHS2.CAND_KY
AND      WHS.RQTN_KY=WHS3.RQTN_KY
AND date(WHS3.RQTN_FILL_DT) between Start_date and End_Date
AND WHS3.TP_OF_OPNG = 'Management'
AND (WHS3.RCTG_CTR = 'Retained' or WHS3.RCTG_CTR IS NULL)
AND WHS3.RQTN_CURR_STAT = 'Filled'
AND substr(WHS3.JOBCODE,1,6) not in ('702609','775400')
AND WHS3.CTRY = 'United States'
AND (select org_lvl_nm_2 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) <> 'Franchise'
order by 2,1
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

