Create Procedure RCTG."SP_MC_MTTF_RETN&BLANK" (IN Start_Date DATE, IN End_Date
DATE) 
 Specific SP_MC_MTTF_RET_BLK 
 Dynamic Result Sets 0 
 Reads SQL Data     
 Language SQL 
Begin
 CURSOR1:BEGIN
        
        DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
SELECT WHS.RQTN_NR "Req #", WHS.PRIM_RECR_NM "Recruiter",
     WHS.POST_TP as "Posting Type",
     date(WHS2.DT_OF_ACPT) "Accept Date", 
     date(WHS.RQTN_APRV_DT) "Req Approval Date", 
     (select count(*) from rctg.whs_dt_dim 
       where dt between date(ext_post_dt) and date(dt_of_acpt) and wkdy_ind = 'Weekday')-1 "Bus Days To Fill",
             (select coalesce(sum(ridqadm.bus_days(date(rqtn_stat_bgdt),date(rqtn_stat_endt))),0)
                 from rctg.whs_rqtn_stat t1,
                          rctg.whs_rqtn_stat_dim t2
                where t2.rqtn_stat_nm = 'Hold'
                   and t1.rqtn_ky = whs2.rqtn_ky
                   and t1.rqtn_stat_ky = t2.rqtn_stat_ky
                   and date(rqtn_stat_bgdt) between date(ext_post_dt) and date(whs2.dt_of_acpt)) "Bus Days on Hold",
           (
     (select count(*) from rctg.whs_dt_dim 
       where dt between date(ext_post_dt) and date(dt_of_acpt) and wkdy_ind = 'Weekday')-1 - 
             (select coalesce(sum(ridqadm.bus_days(date(rqtn_stat_bgdt),date(rqtn_stat_endt))),0)
                 from rctg.whs_rqtn_stat t1,
                          rctg.whs_rqtn_stat_dim t2
                where t2.rqtn_stat_nm = 'Hold'
                   and t1.rqtn_ky = whs2.rqtn_ky
                   and t1.rqtn_stat_ky = t2.rqtn_stat_ky
                   and date(rqtn_stat_bgdt) between date(ext_post_dt) and date(whs2.dt_of_acpt))
             ) "Net Bus Days to Fill",
             WHS.RCTG_CTR as "Recruiting Center",
             org.org_lvl_nm_2 "Region",
      org.org_lvl_nm_5 "Location",
             whs.rqtn_curr_stat as "Req Current Status",
             whs.workgroup_string as "Workgroup",
             date(ext.ext_post_dt) as "External Posting Date",
      ORG.ORG_LVL_ID_5 "Location Code",
      ORG.ORG_LVL_NM_1 "Business Unit Name",
      ORG.ORG_LVL_ID_1 "Business Unit"
   FROM RCTG.WHS_RQTN_DIM WHS, 
     RCTG.WHS_RECR_ACT_FCT WHS2, 
     RCTG.WHS_RECR_EVNT_DIM WHS3 ,
     RCTG.WHS_ORG_DIM ORG,
     (SELECT   whs3.rqtn_ky, whs3.rqtn_nr, min(RS.BEGINDATE) as ext_post_dt
      FROM     RCTG.WHS_RECR_ACT_FCT WHS, 
               RCTG.WHS_RECR_EVNT_DIM WHS1, 
               RCTG.WHS_RQTN_DIM WHS3,
               STAGE_RCTG.REQUISITIONSOURCE RS
      WHERE    WHS.RECR_EVNT_KY=WHS1.RECR_EVNT_KY 
      AND      WHS.RQTN_KY=WHS3.RQTN_KY 
      AND      WHS1.RECR_EVNT_NM = 'Requisition Source'
      AND WHS1.RECR_EVNT_TP = 'External Career Sites Posting'
      AND WHS3.RQTN_ID = RS.REQUISITIONID
      AND RS.SOURCETYPE = '19'
      group by whs3.rqtn_ky, whs3.rqtn_nr) ext
  WHERE date(dt_of_acpt) between Start_Date and End_Date
      AND WHS.RQTN_KY = EXT.RQTN_KY
      AND WHS.RQTN_KY=WHS2.RQTN_KY 
   AND WHS2.RECR_EVNT_KY=WHS3.RECR_EVNT_KY 
   AND WHS.ORG_UNIT_KEY = ORG.ORG_UNIT_KEY
   AND WHS.RQTN_CURR_STAT in ('Filled','Offer')
   AND WHS.TP_OF_OPNG = 'Management' 
--   AND WHS.RCTG_CTR = 'Hewitt'
   AND WHS3.RECR_EVNT_NM = 'Offer' 
   AND WHS2.RECR_ACT_STAT in ('Accepted','Hired-DO NOT USE') 
            AND WHS.POST_TP in ('Internal Posting Only', 'Internal Posting and External Company Website')
   order by 7 desc, 1
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

