                     ) WHS2 on whs.rqtn_ky = whs2.rqtn_ky
 left outer join RCTG.WHS_ORG_DIM ORG on whs2.org_unit_key = org.org_unit_key
 left outer join RCTG.MQT_RECR_ACT_FCT mqt on whs.cand_ky = mqt.cand_ky and whs.rqtn_ky = mqt.rqtn_ky
 inner join (select distinct empl_id, effdt hire_dt, candidate_id, integer(requisition_nbr) req, original_id_value ssn from STAGE_RCTG.SP_PSOFT_UPDATE 
