Create Procedure RCTG.SP_MC_MVCI_SLA_TTS (IN START_DATE DATE, IN END_DATE
DATE) 
 Specific SP_MC_MVCI_SLA_TTS 
 Dynamic Result Sets 0 
 Reads SQL Data   
 Called on Null Input  
 Language SQL 
Begin
 CURSOR1:BEGIN
        
 DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
 select * from (
       select  "Req #",
            jobcode as "Job Code",
            "jobtitle" as "Job Code Title",
            "External Posting Date",        
            case when char(coalesce("Candidate Name",'NULL')) = 'NULL' then 'SLA Not Met'
                  else char("Time to Source") end as "Time to Source (Business Days)",
            "Total Positions",
            "SLA Count Requirements" as "SLA Count Requirement",        
            "Candidate Name",        
            "CANDIDATE ID" as "Applicant ID",
            "Applicant Type",        
            "Event Date" as "Job Bid Date",
            "Region",
      "HOLD BEGIN DATE" as "Hold Begin Date",
      "HOLD END DATE" as "Hold End Date",
            "Location",
            "HR Rep",
            "Req Current Status" as "Requisition Current Status",        
      "Location Code",        
      "Business Unit Name",
         "Business Unit",        
            "Job Function", 
      "Hire Date",  
      "STATE" as "State",
      "Req Open Date",
      "Business Days Open"
            from (
            select    a.rqtn_nr "Req #",
                 a.prim_recr_nm as "Recruiter",
                   jobcode,
             c."jobtitle",
             date(a.ext_post_dt) "External Posting Date",
             ridqadm.add_bus_days(date(a.ext_post_dt),6) as ext_post_dt1,
          char(ridqadm.bus_days(date(a.ext_post_dt),date(b.job_bid_dt))) as "Time to Source",
          c.rqtn_ttl_pstn_ct "Total Positions",
          c.rqtn_ttl_pstn_ct * 3 "SLA Count Requirements",
          b.cand_name "Candidate Name",
          b.cand_id "CANDIDATE ID",
          b.app_type "Applicant Type",
          date(b.job_bid_dt) "Event Date",
          c.region "Region",
          c.location "Location",
          c.rqtn_curr_stat as "Req Current Status",
          c.workgroup_string as "Workgroup",
          c."Location Code",
          c."Business Unit Name",
          c."Business Unit",
          c."Job Function",
          A.HOLD_BEGIN_DATE AS "HOLD BEGIN DATE",
          A.HOLD_END_DATE AS "HOLD END DATE",
          PRIM_HR_REP_NM AS "HR Rep",
          psu.effdt as "Hire Date",
          A.STATE AS "STATE",
          date(a.rqtn_aprv_dt) AS "Req Open Date",
          case when c.rqtn_curr_stat <> 'Filled' then
                    ridqadm.bus_days(date(a.rqtn_aprv_dt),current date -1 day) 
               else
                    ridqadm.bus_days(a.ext_post_dt, date(rqtn_curr_stat_dt)) 
           end as "Business Days Open"
                            from (
            SELECT   distinct whs3.rqtn_ky,
                     whs3.rqtn_nr,
                     whs3.prim_recr_nm,
                     (select min(date(rqtn_stat_bgdt)) 
                     from rctg.whs_rqtn_stat t1, rctg.whs_rqtn_stat_dim t2 
                     where t1.rqtn_stat_ky = t2.rqtn_stat_ky and t2.rqtn_stat_nm = 'Hold' and WHS3.rqtn_ky = t1.rqtn_ky ) as HOLD_BEGIN_DATE,
                     (select min(date(rqtn_stat_endt)) 
                     from rctg.whs_rqtn_stat t1, rctg.whs_rqtn_stat_dim t2 
                     where t1.rqtn_stat_ky = t2.rqtn_stat_ky and t2.rqtn_stat_nm = 'Hold' and WHS3.rqtn_ky = t1.rqtn_ky ) as HOLD_END_DATE,
                     WHS3.PRIM_HR_REP_NM,
                     WHS3.STATE,
                     WHS3.RQTN_APRV_DT,
                     (select min(date(RS.BEGINDATE)) from STAGE_RCTG.REQUISITIONSOURCE RS
                     where WHS3.RQTN_ID = RS.REQUISITIONID
               AND RS.SOURCETYPE = '19'
                     ) as ext_post_dt,
                     rqtn_curr_stat_dt
            FROM     RCTG.WHS_RECR_ACT_FCT WHS,
                     RCTG.WHS_RECR_EVNT_DIM WHS1,
                     RCTG.WHS_RQTN_DIM WHS3       
            WHERE    WHS.RECR_EVNT_KY=WHS1.RECR_EVNT_KY
            AND      WHS.RQTN_KY=WHS3.RQTN_KY
            AND      WHS1.RECR_EVNT_NM = 'Requisition Source'
            AND      WHS1.RECR_EVNT_TP = 'External Career Sites Posting'
               ) as a
           left outer join
               (SELECT  MQT.RQTN_KY, 
                      WHS7.CAND_NM as cand_name,
                      mqt.app_tp as app_type,
                      min(MQT.JOB_BID_DATE) AS job_bid_dt,
                         WHS7.CAND_ID AS cand_id
                FROM   RCTG.MQT_RECR_ACT_FCT MQT,
                    RCTG.WHS_CAND_DIM WHS7 
                WHERE  WHS7.CAND_KY = MQT.CAND_KY
                                              group by MQT.RQTN_KY, mqt.app_tp, WHS7.CAND_ID,  WHS7.CAND_NM
                                    ) as b
          on a.rqtn_ky = b.rqtn_ky
                                        inner join 
                                             (select rqtn_ky, tp_of_opng, rqtn_ttl_pstn_ct, jobcode, rctg_ctr, org.org_lvl_nm_2 region, 
                                                     rqtn_curr_stat, org.org_lvl_nm_5 location, workgroup_string, 
                                                     ORG.ORG_LVL_ID_5 "Location Code", ORG.ORG_LVL_NM_1 "Business Unit Name", 
                                                     ORG.ORG_LVL_ID_1 "Business Unit", job_fn "Job Function", INTN_POST_BUS_TTL "jobtitle" 
                                                     from rctg.whs_rqtn_dim rqtn, rctg.whs_org_dim org
              where rqtn.org_unit_key = org.org_unit_key
              and tp_of_opng = 'Management'
              and POST_TP = 'Internal Posting and External Company Website' 
              and rctg_ctr = 'Hewitt'
              and rqtn_curr_stat not in ('Pending Approval', 'Canceled')
              and substr(rqtn.jobcode,1,6) in ('332260','958020','959101','959201','959301','959401','959501','959601','959199','959299','959399',
                         '959499','959100','959939','959949','959959','967240','967210','959124','959800','959810','959820',
                         '959830','959840','959850','959860','959870','959880','959890','959900','959910','959920','959102',
                         '959940','959302','959919','959950','959930','771990')
            ) as c
          on a.rqtn_ky = c.rqtn_ky
          left outer join 
               STAGE_RCTG.SP_PSOFT_UPDATE PSU 
          on b.CAND_ID = PSU.CANDIDATE_ID AND CHAR(a.RQTN_NR) = PSU.REQUISITION_NBR   
        ) t1
                             where ext_post_dt1 between Start_Date and End_Date
               ) t1
order by 4,1,6
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

