Create Procedure RCTG.SP_MC_POS_FILLED (IN Start_Date DATE, IN End_Date
DATE) 
	Specific SP_MC_POS_FILL 
	Dynamic Result Sets 0 
	Reads SQL Data     
	Language SQL 
Begin
	CURSOR1:BEGIN
        
        DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
SELECT   Distinct 
         A.Offer_Accept_Date Offer_Accept_Date,
         A.Start_Date Start_Date,
         A.Req_Status Req_Status,
         A.Req_Status_Date Req_Status_Date,
         A.RQTN_OPEN_DT Requisition_Open_Date,
         A.Req__ Req_#,
         A.C___of_Positions #_of_Positions,
         A.c6 #_of_Filled_Positions,
         A.ACPT_DD TIME_TO_FILL_POSITION,
         A.ATTP AVERAGE_TIME_TO_FILL_POSITION,
         A.c8 MGMT_OR_NON_MGMT_HOURLY,
         A.Job_Title Job_Title,
         A.c8 Mgmt_or_Non_Mgmt_Hourly,
         A.First_Name First_Name,
         A.Middle_Initial Middle_Initial,
         A.Last_Name Last_Name,
         A.PRIM_ASSMT_RSLT as "Assessment Results/Status",
         (select descr from 
         (select jobcode, setid, MI_ASSESSMENT 
            from stage_Rctg.SP_JOBCODE_TBL jc
           where effdt = (select max(effdt) from stage_Rctg.sp_jobcode_tbl jc1
                           where jc1.setid = jc.setid
                             and jc1.jobcode = jc.jobcode)) t2,
         (select * from stage_rctg.sp_code_descr_tbl
           where FIELDNAME = 'MI_ASSESSMENT'
             and LANGUAGE_CD = 'ENG') t3
         where t2.mi_assessment = t3.fieldvalue
           and substr(a.occ_code,1,6) = t2.jobcode
           and '000' || substr(a.occ_code,8,2) = t2.setid) as "DDI Assessment Type New",
         coalesce((select distinct 'Y' 
         			from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED 
         			where a.RQTN_KY = RAF.RQTN_KY 
         			AND RAF.CAND_KY = a.CAND_KY
         			and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY 
         			AND RED.RECR_EVNT_NM = 'Test' 
         			and RECR_EVNT_TP like 'DDI%' 
         			and recr_act_cmpl_dt is not null),'N') as "DDI Assessment Taken"         ,
         (select recr_act_rslt_1
         			from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED 
         			where a.RQTN_KY = RAF.RQTN_KY 
         			AND RAF.CAND_KY = a.CAND_KY
         			and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY 
         			AND RED.RECR_EVNT_NM = 'Test' 
         			and RECR_EVNT_TP like 'DDI%' 
         			and recr_act_cmpl_dt is not null
				and recr_act_ky = (select max(recr_act_ky) 
									 from rctg.whs_recr_act_fct raf1,
									 	  rctg.whs_recr_evnt_dim red1
									where raf1.recr_evnt_ky = red1.recr_evnt_ky
									  and red1.recr_evnt_tp like 'DDI%'
									  and raf1.rqtn_ky = raf.rqtn_ky
									  and raf1.cand_ky = raf.cand_ky
									  and raf1.recr_act_cmpl_dt is not null)) as "DDI Assessment Result"         ,

         A.OCC_CODE,
         A.c54 Candidate_Email_Address,
         A.Applicant_Type Applicant_Type,
         A.Gender Gender,
         A.EEO EEO,
         A.Source_type Source_type,
         A.Source_Description Source_Description,
         A.c16 Full_Time_Part_Time,
         A.Shift Shift,
         A.Band Band,
         A.Grade Grade,
         A.Zone1 Zone1,
         A.MRP MRP,
         A.Hiring_Manager Hiring_Manager,
         A.c56 Hiring_Manager_Email_Address,
         A.c23 Hiring_Team_HR_Leader,
         A.c24 Role_Maintenance_HR_Leader,
         A.EVP EVP,
         A.Job_Function Job_Function,
         A.Positions_Type Positions_Type,
         A.Justification Justification,
         A.Budgeted Budgeted,
         A.Recruiter_Name Recruiter_Name,
         A.Recruiting_Center Recruiting_Center,
         A.Days_on_Hold Days_on_Hold,
         A.Time_To_Fill_Req Time_To_Fill_Req,
         A.Cost_Per_Hire Cost_Per_Hire,
         A.Country Country,
         A.Work_Group___Brand Work_Group___Brand,
         A.Business_Unit Business_Unit,
         A.Business_Unit_Code Business_Unit_Code,
         A.Region Region,
         A.Market Market,
         A.Cluster Cluster,
         A.Location Location,
         A.Location_Code Location_Code,
         A.Group1 Group1,
         A.Sub_Group Sub_Group,
         A.Specialty Specialty,
         A.Sub_Specialty Sub_Specialty,
         A.Department Department,
         A.Department_Code Department_Code
     
FROM     (SELECT   MQT_RECR_ACT_FCT.DT_OF_ACCEPTANCE Offer_Accept_Date,MQT_RECR_ACT_FCT.ACPT_DD, mqt_recr_act_fct.rqtn_ky, mqt_recr_act_fct.cand_ky,
         WHS_RQTN_DIM.RQTN_CURR_STAT Req_Status,
         WHS_RQTN_DIM.RQTN_OPEN_DT Req_Open_Date,
         CAST(WHS_RQTN_DIM.RQTN_CURR_STAT_DT AS DATE) Req_Status_Date,
         WHS_RQTN_DIM.RQTN_NR Req__,
         WHS_RQTN_DIM.RQTN_TTL_PSTN_CT C___of_Positions,
         WHS_RQTN_DIM.RQTN_PSTN_FILL_CT c6,
         WHS_RQTN_DIM.BUS_TTL Job_Title,
         WHS_RQTN_DIM.TP_OF_OPNG c8,
         WHS_CAND_DIM.CAND_FNM First_Name,
         WHS_CAND_DIM.CAND_MNM Middle_Initial,
         WHS_CAND_DIM.CAND_LNM Last_Name,
         MQT_RECR_ACT_FCT.PRIM_ASSMT_RSLT,
         WHS_CAND_DIM.GDR Gender,
         WHS_CAND_DIM.RACE EEO,
         MQT_RECR_ACT_FCT.CAND_GNRC_SRC Source_type,
         MQT_RECR_ACT_FCT.CAND_SPC_SRC Source_Description,
         WHS_RQTN_DIM.FTPT c16,
         WHS_RQTN_DIM.SHIFT Shift,
         WHS_RQTN_DIM.BAND Band,
         WHS_RQTN_DIM.SLRY_GRD Grade,
         WHS_RQTN_DIM.RQTN_SLRY_ZN Zone1,
         WHS_RQTN_DIM.RQTN_SLRY_MDPN_AT MRP,
         WHS_RQTN_DIM.PRIM_HIRMGR_NM Hiring_Manager,
         WHS_RQTN_DIM.HIRT_HRLDR_NM c23,
         WHS_RQTN_DIM.RPRT_HRLDR_NM c24,
         WHS_RQTN_DIM.EVP EVP,
         WHS_RQTN_DIM.JOB_FN Job_Function,
         WHS_RQTN_DIM.PSTN_TP Positions_Type,
         WHS_RQTN_DIM.RQTN_PSTN_JUST Justification,
         WHS_RQTN_DIM.RQTN_BDGT_ADDT_CT Budgeted,
         WHS_RQTN_DIM.PRIM_RECR_NM Recruiter_Name,
         WHS_RQTN_DIM.RCTG_CTR Recruiting_Center,
         WHS_RQTN_SMRY.RQTN_HOLD_LGTH_DD Days_on_Hold,
         WHS_RQTN_SMRY.RQTN_TTF_LGTH_DD Time_To_Fill_Req,
         WHS_RQTN_SMRY.RQTN_AVG_TTFP_LGTH_DD ATTP,
         MQT_RECR_ACT_FCT.CST_PER_HIR Cost_Per_Hire,
         WHS_RQTN_DIM.CTRY Country,
         WHS_RQTN_DIM.WORKGROUP_STRING Work_Group___Brand,
         WHS_ORG_DIM.ORG_LVL_NM_1 Business_Unit,
         VARCHAR(SUBSTR(WHS_ORG_DIM.ORG_LVL_ID_1, 4 , 2)) Business_Unit_Code,
         WHS_ORG_DIM.ORG_LVL_NM_2 Region,
         WHS_ORG_DIM.ORG_LVL_NM_3 Market,
         WHS_ORG_DIM.ORG_LVL_NM_4 Cluster,
         WHS_ORG_DIM.ORG_LVL_NM_5 Location,
         WHS_ORG_DIM.ORG_LVL_ID_5 Location_Code,
         WHS_ORG_DIM.ORG_LVL_NM_7 Group1,
         WHS_ORG_DIM.ORG_LVL_NM_8 Sub_Group,
         WHS_ORG_DIM.ORG_LVL_NM_9 Specialty,
         WHS_ORG_DIM.ORG_LVL_NM_10 Sub_Specialty,
         WHS_ORG_DIM.ORG_LVL_NM_6 Department,
         WHS_ORG_DIM.ORG_LVL_ID_6 Department_Code,
         MQT_RECR_ACT_FCT.DT_OF_STRT Start_Date,
         WHS_CAND_DIM.CAND_EMAL c54,
         MQT_RECR_ACT_FCT.APP_TP Applicant_Type,
         WHS_RQTN_DIM.RQTN_OPEN_DT,
         WHS_RQTN_DIM.PRIM_HIRMGR_EMAL c56,
         WHS_RQTN_DIM.JOBCODE as OCC_CODE
FROM     RCTG.MQT_RECR_ACT_FCT MQT_RECR_ACT_FCT,
         RCTG.WHS_RQTN_DIM WHS_RQTN_DIM,
         RCTG.WHS_CAND_DIM WHS_CAND_DIM,
         (SELECT WHS_RQTN_SMRY.RQTN_HOLD_LGTH_DD RQTN_HOLD_LGTH_DD, WHS_RQTN_SMRY.RQTN_KY RQTN_KY, WHS_RQTN_SMRY.RQTN_TTF_LGTH_DD RQTN_TTF_LGTH_DD,WHS_RQTN_SMRY.RQTN_AVG_TTFP_LGTH_DD RQTN_AVG_TTFP_LGTH_DD FROM RCTG.WHS_RQTN_SMRY WHS_RQTN_SMRY WHERE WHS_RQTN_SMRY.CURR_FL = 'Y') WHS_RQTN_SMRY,
         RCTG.WHS_ORG_DIM WHS_ORG_DIM
WHERE    (MQT_RECR_ACT_FCT.DT_OF_ACCEPTANCE between Start_Date and End_Date)
AND      MQT_RECR_ACT_FCT.OFF_STAT in ('Accepted', 'Hired - HR Only', 'Hired', 'Hired-DO NOT USE', 'Prehire Complete')
AND      MQT_RECR_ACT_FCT.EXCL_IND = 'N'

AND      WHS_RQTN_DIM.RQTN_KY = WHS_RQTN_SMRY.RQTN_KY
AND      WHS_ORG_DIM.ORG_UNIT_KEY = WHS_RQTN_DIM.ORG_UNIT_KEY
AND      WHS_RQTN_DIM.RQTN_KY = MQT_RECR_ACT_FCT.RQTN_KY
AND      WHS_CAND_DIM.CAND_KY = MQT_RECR_ACT_FCT.CAND_KY
AND      WHS_RQTN_DIM.CTRY = 'United States'
AND 	 WHS_ORG_DIM.ORG_LVL_NM_2 <> 'Franchise'
) A
ORDER BY c8 asc, Req_Status asc, Offer_Accept_Date desc
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

