Create Procedure RCTG.SP_MC_SLA_HATS_RET (IN Start_Date DATE, IN End_Date
DATE) 
 Specific SP_MC_SLA_HATS_RET 
 Dynamic Result Sets 0 
 Reads SQL Data     
 Language SQL 
Begin
 CURSOR1:BEGIN
        
        DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
select * from (
/* Hourly */
SELECT   CAND.CAND_NM "Candidate Name",
         WHS2.RQTN_NR "Requisition Number",
         DATE(WHS2.RQTN_FILL_DT) "Req Filled Date",
         WHS2.TP_OF_OPNG "Requisition Type",
         WHS2.PRIM_RECR_NM as "Recruiter",
         coalesce((select distinct 'Y' from RCTG.WHS_HIRTM H where h.RQTN_KY = WHS2.RQTN_KY and H.HIRTM_MBR_RLE = 'HR Representative'),'N') as "HR Rep Assigned",
         case when mqt.jobbid_stat = 'Pending' then 'N' else 'Y' end as "Eligibility Check Performed",
         mqt.app_tp as "Applicant Type",
         coalesce((select distinct 'Y' from rctg.whs_recr_act_fct raf where ((raf.recr_act_stat_rsn like '%Rapid Hire') or (raf.recr_act_stat like '%Rapid Hire')) and raf.cand_ky = whs.cand_ky and raf.rqtn_ky = whs.rqtn_ky),'N') as "Rapid Hire?",
--         case when WHS.RECR_ACT_STAT_RSN = 'Rapid Hire' then 'Y' else 'N' end as "Rapid Hire?",
         WHS2.JOB_FN "Job Function",
         org.org_lvl_nm_2 "Region",
  ORG.ORG_LVL_NM_5 "Location",
  ORG.ORG_LVL_NM_1 "Business Unit Name",
  ORG.ORG_LVL_ID_1 "Business Unit",
  WHS2.BUS_TTL "Job Title",
         (select t1.person_id from STAGE_RCTG.SP_PERSON_ID_TBL t1, STAGE_RCTG.SP_PERSONAL_DATA_CURR_V t2  where t1.person_id = t2.person_id and t2.person_type = 'EE' and t1.id_type = 'PR'
           and t1.id_value = ps.ssn) person_id,
         DATE(WHS.DT_OF_ACPT) "Date of Accept",
         WHS2.JOBCODE,
        PS.HIRE_DT as "PS_HIRE_DT",
        MQT.BGC_RSLT,
        MQT.DRUG_RSLT,
--        MQT.REF_CHK_RSLTS,
        (select recr_act_stat from rctg.whs_recr_act_fct t1, rctg.whs_recr_evnt_dim t2 where t1.recr_evnt_ky = t2.recr_evnt_ky
                and t2.recr_evnt_nm = 'Reference Check' 
                and t2.recr_evnt_tp = 'CKR'

                and t1.cand_ky = whs.cand_ky and t1.rqtn_ky = whs.rqtn_ky) as REF_CHK_RSLTS,
        rqtn_curr_stat as "Req Current Status",
        CAND.CURR_JOB_FN_NM as "Current Job Function",
        CAND.CAND_CURR_LOC_DS as "Current Location",
        CAND.CAND_CURR_OCC_CD as "Current OCC Code",
        ORG.ORG_LVL_ID_5 as "Location Code"
FROM     
      (select distinct * from RCTG.WHS_RECR_ACT_FCT where recr_act_stat = 'Accepted') WHS
 inner join (select distinct * from RCTG.WHS_RECR_EVNT_DIM where recr_evnt_nm = 'Offer') WHS1 on whs.recr_evnt_ky = whs1.recr_evnt_ky
 inner join RCTG.WHS_CAND_DIM CAND on whs.cand_ky = cand.cand_ky
 inner join (select * from RCTG.WHS_RQTN_DIM where (rctg_ctr = 'Retained' or rctg_ctr is null)
     and tp_of_opng like 'Hourly%' 
                    and rqtn_curr_stat not in ('Pending Approval', 'Canceled') ) WHS2 on whs.rqtn_ky = whs2.rqtn_ky
 left outer join RCTG.WHS_ORG_DIM ORG on whs2.org_unit_key = org.org_unit_key
 left outer join RCTG.MQT_RECR_ACT_FCT mqt on whs.cand_ky = mqt.cand_ky and whs.rqtn_ky = mqt.rqtn_ky
 inner join (select distinct empl_id, effdt hire_dt, candidate_id, integer(requisition_nbr) req, original_id_value ssn from STAGE_RCTG.SP_PSOFT_UPDATE where effdt between Start_Date and End_Date) PS on ps.candidate_id = cand.cand_id and ps.req = whs2.rqtn_nr 
 ) t1
;--
OPEN DUMMY_CURSOR;--
END CURSOR1;--
end;

