Create Procedure RCTG.SP_MC_SLA_MATS (IN Start_Date DATE, IN End_Date DATE)

	Specific SP_MC_SLA_MATS 
	Dynamic Result Sets 0 
	Reads SQL Data     
	Language SQL 
Begin
	CURSOR1:BEGIN
        
        DECLARE DUMMY_CURSOR CURSOR WITH RETURN FOR
SELECT   WHS3.RQTN_NR as "Requisition Number",
         date(WHS3.RQTN_FILL_DT) as "Req Fill Date",
         WHS3.BUS_TTL as "Title",
         WHS3.JOB_FN as "Job Function",
         WHS3.PRIM_RECR_NM as "Recruiter Name",
         coalesce((select distinct 'Y' from RCTG.WHS_HIRTM H where h.RQTN_KY = WHS3.RQTN_KY and H.HIRTM_MBR_RLE = 'Hiring Manager'),'N') as "Hiring Manager Assigned",
