         WHS.SCREEN_TYPE as "Screen Type",
         WHS.INTRVW_DT as "Interview Date",
         WHS.INTRVW_STAT as "Interview Status",
         WHS.INTRVW_STAT_DT as "Interview Status Date",
         WHS.INTRVW_TP as "Interview Type",
         WHS.OFF_STAT as "Offer Status",
         WHS.BGC_RSLT as "BC Result",
         WHS.BGC_COMPL_DT as "BC Completion Date",
         WHS.DRUG_RSLT as "DT Result",
         WHS.DRUG_TEST_COMPL_DT as "DT Completion Date",
         WHS.REF_CHK_RSLTS as"Ref Chk Results",
         coalesce((select distinct 'Y' from stage_rctg.requisitionsource rs where rs.requisitionid = whs3.rqtn_id and sourcetype = '16'),'N') as "Source Internal?",
         coalesce((select distinct 'Y' from stage_rctg.requisitionsource rs where rs.requisitionid = whs3.rqtn_id and sourcetype = '19' and (days(enddate) - days(begindate)>=2)),'N') as "Source External for 2 days?",
         (select org_lvl_id_1 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Business Unit",
         (select org_lvl_nm_1 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Business Unit Name",
         (select org_lvl_nm_5 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Location",
         (select org_lvl_nm_2 from rctg.whs_org_dim org where org.org_unit_key = whs3.org_unit_key) as "Region",
         coalesce((select distinct 'Y' from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED where WHS.RQTN_KY = RAF.RQTN_KY AND RAF.CAND_KY = WHS.CAND_KY and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY AND RED.RECR_EVNT_NM = 'Test' and RECR_EVNT_TP like 'DDI%' and recr_act_cmpl_dt is not null),'N') as "DDI Assessment Taken",
         (select recr_evnt_tp from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED where WHS.RQTN_KY = RAF.RQTN_KY AND RAF.CAND_KY = WHS.CAND_KY and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY AND RED.RECR_EVNT_NM = 'Test' and RECR_EVNT_TP like 'DDI%' and recr_act_cmpl_dt is not null and recr_act_ky = (select max(recr_act_ky) from rctg.whs_recr_act_fct raf1, rctg.whs_recr_evnt_dim red1 where raf1.recr_evnt_ky = red1.recr_evnt_ky and red1.recr_evnt_tp like 'DDI%' and raf1.rqtn_ky = raf.rqtn_ky and raf1.cand_ky = raf.cand_ky and raf1.recr_act_cmpl_dt is not null)) as "DDI Assessment Type",
         (select recr_act_rslt_1 from RCTG.WHS_RECR_ACT_FCT RAF, RCTG.WHS_RECR_EVNT_DIM RED where WHS.RQTN_KY = RAF.RQTN_KY AND RAF.CAND_KY = WHS.CAND_KY and RAF.RECR_EVNT_KY = RED.RECR_EVNT_KY AND RED.RECR_EVNT_NM = 'Test' and RECR_EVNT_TP like 'DDI%' and recr_act_cmpl_dt is not null and recr_act_ky = (select max(recr_act_ky) from rctg.whs_recr_act_fct raf1, rctg.whs_recr_evnt_dim red1 where raf1.recr_evnt_ky = red1.recr_evnt_ky and red1.recr_evnt_tp like 'DDI%' and raf1.rqtn_ky = raf.rqtn_ky and raf1.cand_ky = raf.cand_ky and raf1.recr_act_cmpl_dt is not null)) as "DDI Assessment Result",
