         (select descr from (select jobcode, setid, MI_ASSESSMENT from stage_Rctg.SP_JOBCODE_TBL jc where effdt = (select max(effdt) from stage_Rctg.sp_jobcode_tbl jc1 where jc1.setid = jc.setid and jc1.jobcode = jc.jobcode)) t2, (select * from stage_rctg.sp_code_descr_tbl where FIELDNAME = 'MI_ASSESSMENT' and LANGUAGE_CD = 'ENG') t3 where t2.mi_assessment = t3.fieldvalue and substr(whs3.jobcode,1,6) = t2.jobcode and '000' || substr(whs3.jobcode,8,2) = t2.setid) as "DDI Assessment Type New"
