DECLARE V_NO_DATA INT DEFAULT 0;--
DECLARE VAR_DT DATE;--
DECLARE VAR_CAND INTEGER;--
DECLARE VAR_RQTN INTEGER;--
DECLARE VAR_ERROR VARCHAR(4000);--
DECLARE not_found CONDITION FOR '02000';--
DECLARE CUR_REQ_EXCEP CURSOR WITH RETURN FOR
SELECT   rqtn_ky,           error_codes  FROM     (   SELECT   rqtn.rqtn_ky,           replace( coalesce(a.err_cd,' ') || coalesce(c.err_cd,' ') || coalesce(d.err_cd,' ') || coalesce(e.err_cd,' ') || coalesce(f.err_cd,' ') || coalesce(g.err_cd,' ') || coalesce(h.err_cd,' ') || coalesce(i.err_cd,' ') || coalesce(j.err_cd,' ') || coalesce(k.err_cd,' ') || coalesce(l.err_cd,' ') || coalesce(m.err_cd,' ') || coalesce(n.err_cd,' ') || coalesce (o.err_cd,' ') || coalesce(p.err_cd,' ') || coalesce(q.err_cd,' ') || coalesce(r.err_cd,' ') || coalesce(s.err_cd,' ') || coalesce(t.err_cd,' ') || coalesce(u.err_cd,' ') || coalesce(v.err_cd,' '),' ','') as error_codes,           rqtn_cret_dt  FROM     rctg.whs_rqtn_dim as rqtn            LEFT OUTER JOIN ( SELECT rqtn_ky, 'A' as err_cd FROM (SELECT rqtn_ky, case when left( right(jobcode,length(jobcode) - locate('-', jobcode) ),2) = right ( RQTN_FLAT_ORG_ID_1,2) then 1 else 0 end as errcd0 FROM RCTG.WHS_RQTN_DIM O) t1 WHERE errcd0 = 0) as a on rqtn.rqtn_ky = a.rqtn_ky            
LEFT OUTER JOIN ( Select rqtn_ky, 'D' as err_cd from RCTG.WHS_RQTN_DIM where intk_sess_dt is null and date(current date) > date( rqtn_aprv_dt + 5 days) ) as d on rqtn.rqtn_ky = d.rqtn_ky            
LEFT OUTER JOIN ( SELECT rqtn_ky, 'E' as err_cd FROM RCTG.WHS_HIRTM t1 WHERE TERM_FL = 'Y' AND CURR_FL = 'Y' AND HIRTM_MBR_RLE = 'Hiring Manager' ) as e on rqtn.rqtn_ky = e.rqtn_ky            
LEFT OUTER JOIN ( SELECT t1.RQTN_KY, 'F' as err_cd FROM RCTG.WHS_RQTN_DIM T1            LEFT OUTER JOIN ( SELECT * from rctg.whs_hirtm where HIRTM_MBR_RLE = 'HR Manager' and CURR_FL = 'Y' and TERM_FL = 'N' ) t2 on T1.RQTN_KY = T2.RQTN_KY where TERM_FL is null ) as f on rqtn.rqtn_ky = f.rqtn_ky            
LEFT OUTER JOIN ( SELECT RQTN_ky, 'G' as err_cd FROM RCTG.WHS_RQTN_DIM WHERE TP_OF_OPNG = 'Management' AND RQTN_KY NOT IN ( SELECT DISTINCT RQTN_KY FROM RCTG.WHS_HIRTM WHERE HIRTM_MBR_RLE = 'Requisition Approver') ) as g on rqtn.rqtn_ky = g.rqtn_ky            
LEFT OUTER JOIN ( SELECT RQTN_KY, 'H' as err_cd FROM ( SELECT RQTN_ky, CASE WHEN TP_OF_OPNG = 'Management' AND ((date(CURRENT DATE) - date(RQTN_CURR_STAT_DT)) > 30 ) THEN 1 WHEN TP_OF_OPNG = 'Hourly/Non-Management' AND ((date(CURRENT DATE) - date( RQTN_CURR_STAT_DT)) > 14) THEN 1 else 0 end as errcd FROM RCTG.WHS_RQTN_DIM WHERE RQTN_CURR_STAT = 'Hold') t1 WHERE errcd = 1 ) as h on rqtn .rqtn_ky = h.rqtn_ky            
LEFT OUTER JOIN ( SELECT RQTN_KY, 'I' as err_cd FROM RCTG.WHS_RQTN_DIM WHERE PRIM_RECR_NM = 'Services, Recruiting' AND date( RQTN_APRV_DT) + 1 day > current date ) as i on rqtn.rqtn_ky = i.rqtn_ky            
LEFT OUTER JOIN ( SELECT distinct t2.rqtn_ky, 'J' as err_cd FROM RCTG.WHS_RECR_ACT_FCT t1, RCTG.whs_rqtn_dim t2 WHERE t1.rqtn_ky = t2.rqtn_ky AND DT_OF_ACPT < RQTN_APRV_DT + 3 days ) as j on rqtn.rqtn_ky = j.rqtn_ky            
LEFT OUTER JOIN ( SELECT WHS1.RQTN_KY, 'K' as err_cd FROM RCTG.WHS_HIRTM WHS, RCTG.WHS_RQTN_DIM WHS1 WHERE WHS.RQTN_KY=WHS1.RQTN_KY AND WHS1.RQTN_CURR_STAT not in ('Canceled','Filled') AND WHS.DECN = 'Approved With Conditions' AND WHS1.TP_OF_OPNG = 'Management' ) as k on rqtn .rqtn_ky = k.rqtn_ky            
LEFT OUTER JOIN ( SELECT t1.rqtn_ky, 'L' as err_cd FROM RCTG.whs_rqtn_dim t1, rctg.whs_rqtn_smry t2 WHERE t1.rqtn_ky = t2.rqtn_ky AND t2.curr_fl = 'Y' AND t1.tp_of_opng = 'Management' AND t1.intk_sess_dt is not null AND t1.rqtn_curr_stat not in ('Filled', 'Canceled', 'Pending Approval' ) ) as l on rqtn.rqtn_ky = l.rqtn_ky            
LEFT OUTER JOIN ( Select rqtn_nr, 'M' as err_cd from stage_rctg.req_posting_error ) as m on rqtn.rqtn_nr = m.rqtn_nr            
LEFT OUTER JOIN ( SELECT rqtn_ky, 'N' as err_cd FROM RCTG.WHS_RQTN_DIM WHERE TP_OF_OPNG = 'Management' AND POST_TP in ( 'J-1 Trainee (HR Only)', 'College Intern (HR Only)', 'Other Employment Program (HR Only)', 'H2B (HR Only)') ) as n on rqtn.rqtn_ky = n.rqtn_ky            
LEFT OUTER JOIN ( SELECT rqtn_ky, 'O' as err_cd FROM RCTG.WHS_RQTN_DIM WHERE WORKGROUP_STRING NOT IN ( 'MI College Recruiting', 'MVCI College Recruiting') AND POST_TP = 'College Management Development Program (HR Only)' ) as o on rqtn.rqtn_ky = o.rqtn_ky            
LEFT OUTER JOIN ( SELECT rqtn_ky, 'P' as err_cd FROM RCTG.WHS_RQTN_DIM t1 WHERE ( select count(*) from RCTG.WHS_HIRTM t2 WHERE t1.rqtn_ky = t2.rqtn_ky and t2.hirtm_mbr_rle like 'HR%') = 0 ) as p on rqtn.rqtn_ky = p.rqtn_ky            
LEFT OUTER JOIN ( SELECT A.RQTN_KY, 'Q' as ERR_CD FROM ( SELECT RQTN_KY FROM RCTG.WHS_RQTN_DIM WHERE TP_OF_OPNG = 'Hourly/Non-Management' AND CTRY='United States') R, ( SELECT RQTN_KY, RECR_EVNT_KY FROM RCTG.WHS_RECR_ACT_FCT WHERE CURR_EVNT_FL='Y' AND SPC_SRC IN ('Career Portal','Marriott Company Website' , 'The Ritz-Carlton Company Website', 'the MVCI corporate website (Aug 2007)') AND DATE(RECR_ACT_DT) > DATE( CURRENT DATE)) A, RCTG.WHS_RECR_EVNT_DIM E WHERE R.RQTN_KY=A.RQTN_KY AND A.RECR_EVNT_KY=E.RECR_EVNT_KY AND E.RECR_EVNT_NM='Requisition Source' ) AS Q ON RQTN.RQTN_KY=Q.RQTN_KY            
LEFT OUTER JOIN ( SELECT Distinct A.RQTN_KY, 'R' as ERR_CD, RQTN_FLAT_ORG_ID_1, SPC_SRC FROM ( SELECT RQTN_KY, RQTN_FLAT_ORG_ID_1 FROM RCTG.WHS_RQTN_DIM WHERE RIGHT(RQTN_FLAT_ORG_ID_1,2) NOT IN ('73','74') ) R, ( SELECT RQTN_KY,RECR_EVNT_KY, SPC_SRC FROM RCTG.WHS_RECR_ACT_FCT WHERE RECR_ACT_STAT in ('Posted','Re-Posting','Re-Posted') AND SPC_SRC = 'The Ritz-Carlton Company Website') A, RCTG.WHS_RECR_EVNT_DIM E WHERE R.RQTN_KY=A.RQTN_KY AND A.RECR_EVNT_KY=E.RECR_EVNT_KY AND E.RECR_EVNT_NM= 'Requisition Source' UNION SELECT Distinct A.RQTN_KY, 'R' as ERR_CD, rqtn_flat_org_id_1, spc_src FROM ( SELECT RQTN_KY, rqtn_flat_org_id_1 FROM RCTG.WHS_RQTN_DIM WHERE RIGHT(RQTN_FLAT_ORG_ID_1,2) <> '38') R, ( SELECT RQTN_KY,RECR_EVNT_KY, spc_src FROM RCTG.WHS_RECR_ACT_FCT WHERE RECR_ACT_STAT in ('Posted','Re-Posting','Re-Posted') AND SPC_SRC = 'MVCI Company Website') A, RCTG.WHS_RECR_EVNT_DIM E WHERE R.RQTN_KY=A.RQTN_KY AND A.RECR_EVNT_KY=E.RECR_EVNT_KY AND E.RECR_EVNT_NM= 'Requisition Source' ) AS R ON RQTN.RQTN_KY =R.RQTN_KY            LEFT OUTER JOIN ( SELECT Distinct RQTN_KY, 'C' as err_cd FROM ( SELECT WHS.RQTN_KY, WHS.RQTN_NR, WHS.RQTN_SLRY_ZN, case when WHS.RQTN_SLRY_ZN = 'Zone 1' then 'LZ1' when WHS.RQTN_SLRY_ZN = 'Zone 2' then 'LZ2' when WHS.RQTN_SLRY_ZN = 'Zone 3' then 'LZ3' when WHS.RQTN_SLRY_ZN = 'Zone 4' then 'LZ4' else 'Select One' end as zone, WHS.RQTN_FLAT_ORG_ID_4, SP_.SAL_ADMIN_PLAN, CTRY, TP_OF_OPNG FROM RCTG.WHS_RQTN_DIM WHS, STAGE_RCTG.SP_LOCATION_TBL SP_ WHERE WHS.CTRY = 'United States' AND WHS.TP_OF_OPNG = 'Management' AND SP_.SETID = 'MIGBL' AND substr(WHS.RQTN_FLAT_ORG_ID_4,1,locate('-' , WHS.RQTN_FLAT_ORG_ID_4)-1)=SP_.LOCATION AND SP_.EFFDT = (SELECT MAX(EFFDT) FROM STAGE_RCTG.SP_LOCATION_TBL SP_1 WHERE SP_1.SETID = SP_.SETID AND SP_1.LOCATION = SP_.LOCATION)) t1 WHERE zone <> SAL_ADMIN_PLAN ) as C ON RQTN.RQTN_KY = C.RQTN_KY           
LEFT OUTER JOIN ( SELECT T1.RQTN_KY, 'S' as ERR_CD FROM RCTG.WHS_RQTN_DIM T1, STAGE_RCTG.REQ_EXC_S T2                              WHERE T1.RQTN_NR = T2.RQTN_NR) as S on RQTN.RQTN_KY = S.RQTN_KY           
LEFT OUTER JOIN ( SELECT DISTINCT T1.RQTN_KY, 'T' as ERR_CD FROM RCTG.WHS_RQTN_DIM T1, RCTG.WHS_HIRTM T2                              WHERE T1.RQTN_KY = T2.RQTN_KY                                AND T1.RQTN_CURR_STAT in ('Open','ReOpen')                                AND T2.TERM_FL = 'Y'                                AND T2.HIRTM_MBR_RLE = 'Hiring Manager') as T on RQTN.RQTN_KY = T.RQTN_KY           
LEFT OUTER JOIN ( SELECT DISTINCT T1.RQTN_KY, 'U' as ERR_CD FROM RCTG.WHS_RQTN_DIM T1, RCTG.WHS_HIRTM T2                              WHERE T1.RQTN_KY = T2.RQTN_KY                                AND T1.RQTN_CURR_STAT in ('Open','ReOpen')                                AND T2.TERM_FL = 'Y'                                AND T2.HIRTM_MBR_RLE = 'Recruiter') as U on RQTN.RQTN_KY = U.RQTN_KY           
LEFT OUTER JOIN ( SELECT DISTINCT T1.RQTN_KY, 'V' as ERR_CD FROM RCTG.WHS_RQTN_DIM T1, RCTG.WHS_HIRTM T2                              WHERE T1.RQTN_KY = T2.RQTN_KY                                AND T1.RQTN_CURR_STAT in ('Open','ReOpen')                                AND T2.TERM_FL = 'Y'                                AND T2.HIRTM_MBR_RLE = 'HR Manager') as V on RQTN.RQTN_KY = V.RQTN_KY                      ) t1  WHERE    error_codes <> ' '  AND      date(t1.rqtn_cret_dt) >= date('2007-07-16');--
DECLARE CONTINUE HANDLER FOR NOT FOUND SET V_NO_DATA = 1;--
/*call truncate('RCTG','WHS_tbnt_fct'); */
delete from RCTG.WHS_REQ_EXCEPTION;--
SET V_NO_DATA = 0;--
OPEN CUR_REQ_EXCEP;--
FETCH CUR_REQ_EXCEP INTO VAR_RQTN, VAR_ERROR;--
WHILE (V_NO_DATA = 0) DO
  INSERT INTO RCTG.WHS_REQ_EXCEPTION VALUES( VAR_RQTN, VAR_ERROR);--
  FETCH CUR_REQ_EXCEP INTO VAR_RQTN, VAR_ERROR;--
END WHILE;--
CLOSE CUR_REQ_EXCEP;--
commit;--
END ;

