       RQTN_REQD_BC = V_BCREQ3
       THEN V_BCREQ3
     when
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ4 AND
       LOC_LVL = V_LOC_LVL_3
       THEN V_BCREQ3
     when
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ4 AND
       LOC_LVL = V_LOC_LVL_1
