        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when --10
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN V_BCREQ5
     when --11
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when --12
