         (select * from stage_rctg.sp_code_descr_tbl
           where FIELDNAME = 'MI_BACKGRND_CHECK'
             and LANGUAGE_CD = 'ENG') t3
         where t2.mi_backgrnd_check = t3.fieldvalue
         ) rjc on '000' || substr(t3.jobcode,8,2) = rjc.setid and substr(t3.jobcode,1,6) = rjc.jobcode 
left outer join (
         select setid, jobcode, descr CURR_JC_REQD_BC
         from 
