update RCTG.MQT_RECR_ACT_FCT MQT
SET PRE_EMP_REQ_MET =           
(           
SELECT 
CASE 
 -- SET FLAG to '' IF PS START DATE IS NULL
 WHEN PS_STRT_DT IS NULL 
    THEN ''
 WHEN 
   BC_REQ in (V_BCREQ6, V_BCREQ1) AND
   ((DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL 
     AND PS_STRT_DT IS NOT NULL 
     AND DRG_TST_DT < PS_STRT_DT) OR
    (DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL 
     AND PS_STRT_DT IS NULL) OR
    (DRG_TST_REQ = 'N')
   )
   THEN 'Y'
 WHEN 
   BC_REQ = V_BCREQ5 AND
   ((BC_DT IS NOT NULL 
     AND PS_STRT_DT IS NOT NULL
     AND BC_DT < PS_STRT_DT) 
    OR
    (BC_DT IS NULL)
   ) AND
   ((DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND DRG_TST_DT < PS_STRT_DT) OR
    (DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NULL) OR
    (DRG_TST_REQ = 'N')
   )
   THEN 'Y'
 WHEN
   BC_REQ = V_BCREQ2 AND
   BC_PKG in (V_BC_PKG2, V_BC_PKG3) AND
   ((BC_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND BC_DT < PS_STRT_DT) OR
    (BC_DT IS NOT NULL
     AND PS_STRT_DT IS NULL)) AND
   ((DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND DRG_TST_DT < PS_STRT_DT) OR
    (DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NULL) OR
    (DRG_TST_REQ = 'N')
   )
   THEN 'Y'
 WHEN
   BC_REQ = V_BCREQ3 AND
   BC_PKG in (
   V_BC_PKG2, 
   V_BC_PKG3, 
   V_BC_PKG4, 
   V_BC_PKG5, 
   V_BC_PKG6, 
   V_BC_PKG7) AND
   ((BC_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND BC_DT < PS_STRT_DT) OR
    (BC_DT IS NOT NULL
     AND PS_STRT_DT IS NULL)) AND
   ((DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND DRG_TST_DT < PS_STRT_DT) OR
    (DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NULL) OR
    (DRG_TST_REQ = 'N')
   )
   THEN 'Y'
 WHEN
   BC_REQ = V_BCREQ4 AND
   BC_PKG in (
   V_BC_PKG1, 
   V_BC_PKG2, 
   V_BC_PKG3, 
   V_BC_PKG4, 
   V_BC_PKG5, 
   V_BC_PKG6, 
   V_BC_PKG7) AND
   BC_DT < PS_STRT_DT AND
   ((BC_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND BC_DT < PS_STRT_DT) OR
    (BC_DT IS NOT NULL
     AND PS_STRT_DT IS NULL)) AND
   ((DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NOT NULL
     AND DRG_TST_DT < PS_STRT_DT) OR
    (DRG_TST_REQ = 'Y' 
     AND DRG_TST_DT IS NOT NULL
     AND PS_STRT_DT IS NULL) OR
    (DRG_TST_REQ = 'N')
   )
   THEN 'Y'
    ELSE 'N'
    END AS PRE_EMP_REQ_MET
FROM 
  RCTG.MQT_RECR_ACT_FCT MQT1
  WHERE MQT1.CAND_KY = MQT.CAND_KY
    AND MQT1.RQTN_KY = MQT.RQTN_KY
    
    fetch first 1 rows only 
    )
    where off_stat is not null;                 --

/* IF BC and DT not Required, the set to 'Y' */                   
update RCTG.MQT_RECR_ACT_FCT MQT
SET PRE_EMP_REQ_MET = 'Y'           
where off_stat is not null
    and BC_REQ in (V_BCREQ6, V_BCREQ1) 
    AND DRG_TST_REQ = 'N';--
End  ;

