Create Procedure RCTG.SP_UPDATE_MQT_PREHIRE_CHK_V1 (IN PARM1 INTEGER) 
 Dynamic Result Sets 0 
 Modifies SQL Data   
 Called on Null Input  
 Language SQL 
Begin
DECLARE V_EXT VARCHAR(8) Default 'External';--
DECLARE V_INT VARCHAR(8) Default 'Internal';--
DECLARE V_LOC_LVL_0 CHAR(1) Default '0';--
DECLARE V_LOC_LVL_1 CHAR(1) Default '1';--
DECLARE V_LOC_LVL_2 CHAR(1) Default '2';--
DECLARE V_LOC_LVL_3 CHAR(1) Default '3';--
DECLARE V_BCREQ1 VARCHAR(65) Default 'International-N/A';--
DECLARE V_BCREQ2 VARCHAR(65) Default 'Executive';--
DECLARE V_BCREQ3 VARCHAR(65) Default 'TRUSST-Criminal';--
DECLARE V_BCREQ4 VARCHAR(65) Default 'RSO-Sexual Offender';--
DECLARE V_BCREQ5 VARCHAR(65) Default 'Required if not previously completed';--
DECLARE V_BCREQ6 VARCHAR(65) Default 'Not Required';--
DECLARE V_BCREQ7 VARCHAR(65) Default 'None';--
DECLARE V_BC_PKG1 VARCHAR(65) Default 'External - Sexual Offender Check Only';--
