DECLARE V_BC_PKG3 VARCHAR(65) Default 'Internal - Exec BG Check - (First Time Required)';--
DECLARE V_BC_PKG4 VARCHAR(65) Default 'External (Security) - Sexual Offender Check & Full BG Check';--
DECLARE V_BC_PKG5 VARCHAR(65) Default 'External (Non-Security) - Sexual Offender Check & Full BG Check';--
DECLARE V_BC_PKG6 VARCHAR(65) Default 'Internal (Security) - Full BG Check (First Time Required)';--
DECLARE V_BC_PKG7 VARCHAR(65) Default 'Internal (Non-Security) - Full BG Check (First Time Required)';--
DECLARE V_NO_DATA INT DEFAULT 0;--
DECLARE V_BC_REQ VARCHAR(100);--
DECLARE V_CAND_KY INTEGER;--
DECLARE V_RQTN_KY INTEGER;--
DECLARE CUR_REQ_BC CURSOR WITH RETURN FOR
Select 
t1.cand_ky,
t1.rqtn_ky,
case when 
         app_tp = V_EXT AND
         LOC_LVL = V_LOC_LVL_0
         THEN V_BCREQ1
     when 
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ7
       THEN V_BCREQ1
     when 
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ2
       THEN V_BCREQ2
     when 
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ3
       THEN V_BCREQ3
     when
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ4 AND
       LOC_LVL = V_LOC_LVL_3
       THEN V_BCREQ3
     when
       app_tp = V_EXT AND
       RQTN_REQD_BC = V_BCREQ4 AND
       LOC_LVL = V_LOC_LVL_1
       THEN V_BCREQ4
     when 
       app_tp = V_INT AND
       LOC_LVL = V_LOC_LVL_0
       THEN V_BCREQ1
     when 
       app_tp = V_INT AND
       RQTN_REQD_BC = V_BCREQ7
       THEN V_BCREQ1
     when 
       app_tp = V_INT AND
       RQTN_REQD_BC = V_BCREQ2 AND
       CURR_JC_REQD_BC = V_BCREQ2
       THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ2 AND
        CURR_JC_REQD_BC <> V_BCREQ2
        THEN V_BCREQ2
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        CURR_JC_REQD_BC = V_BCREQ3 
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        CURR_JC_REQD_BC = V_BCREQ2
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_1 
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3) AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ4 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3, V_BCREQ4) AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN V_BCREQ6
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3, V_BCREQ4) AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ6
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3, V_BCREQ4, V_BCREQ7) AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ6
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ6
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_1 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN V_BCREQ6
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC IN (V_BCREQ4, V_BCREQ7) AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3) AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_1
        THEN V_BCREQ3
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ4 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC IN (V_BCREQ2, V_BCREQ3, V_BCREQ4, V_BCREQ7) AND
        CURR_LOC_LVL = V_LOC_LVL_3
        THEN RQTN_REQD_BC || ' - ' || V_BCREQ5
     when 
        app_tp = V_INT AND
        RQTN_REQD_BC = V_BCREQ3 AND
        LOC_LVL = V_LOC_LVL_3 AND
        CURR_JC_REQD_BC = V_BCREQ7 AND
        CURR_LOC_LVL = V_LOC_LVL_0
        THEN V_BCREQ2
     END as BC_REQ
from RCTG.MQT_RECR_ACT_FCT t1
inner join RCTG.WHS_CAND_DIM cand on cand.cand_ky = t1.cand_ky
inner join (select t2.cand_ky, t2.mii_emplid, ps.setid_jobcode, ps.jobcode, cand_curr_loc from RCTG.WHS_CAND_DIM t2
            left outer join stage_rctg.ps_employees ps
            on t2.mii_emplid = ps.emplid and 0 = ps.empl_rcd) t2 on t1.cand_ky = t2.cand_ky
inner join RCTG.MQT_RQTN_UTIL t3 on t1.rqtn_ky = t3.rqtn_ky
left outer join (select location, mi_background_auth as LOC_LVL from STAGE_RCTG.PS_LOCATION_TBL) T4
 on t4.location = t3.location
left outer join (select location, mi_background_auth as CURR_LOC_LVL from STAGE_RCTG.PS_LOCATION_TBL) T5
 on t5.location = t2.cand_curr_loc
left outer join (
         select setid, jobcode, descr as RQTN_REQD_BC
         from 
          STAGE_RCTG.PS_JOBCODE_TBL_UTIL1 t2,
         (select * from stage_rctg.sp_code_descr_tbl
           where FIELDNAME = 'MI_BACKGRND_CHECK'
             and LANGUAGE_CD = 'ENG') t3
         where t2.mi_backgrnd_check = t3.fieldvalue
         ) rjc on t3.bu = rjc.setid and t3.jobcode = rjc.jobcode 
left outer join (
         select setid, jobcode, descr CURR_JC_REQD_BC
         from 
          STAGE_RCTG.PS_JOBCODE_TBL_UTIL1 t2,
         (select * from stage_rctg.sp_code_descr_tbl
           where FIELDNAME = 'MI_BACKGRND_CHECK'
             and LANGUAGE_CD = 'ENG') t3
         where t2.mi_backgrnd_check = t3.fieldvalue
         ) rjc1 on t2.setid_jobcode = rjc1.setid and t2.jobcode = rjc1.jobcode
where ((t1.OFF_STAT is not null) OR
	   (t1.INTRVW_STAT = 'Interest' and cand.SRC_SYS_ID = 'U' and t1.INTRVW_TP = 'Hiring Manager Interview')
      );--
DECLARE CONTINUE HANDLER FOR NOT FOUND SET V_NO_DATA = 1;--

/* BC_REQ UPDATE */
SET V_NO_DATA = 0;--
OPEN CUR_REQ_BC;--
FETCH CUR_REQ_BC INTO V_CAND_KY, V_RQTN_KY, V_BC_REQ;--
