  UPDATE RCTG.MQT_RECR_ACT_FCT MQT
     SET BC_REQ = V_BC_REQ
   WHERE MQT.RQTN_KY = V_RQTN_KY
     AND MQT.CAND_KY = V_CAND_KY;--
  FETCH CUR_REQ_BC INTO V_CAND_KY, V_RQTN_KY, V_BC_REQ;--
END WHILE;--
CLOSE CUR_REQ_BC;   --
    
/* PS_STRT_DT Updates */    
update rctg.mqt_recr_act_fct raf
set PS_STRT_DT = (
      select 
       min(PSJ.EFFDT) as PS_STRT_DT
      from 
       (select cand_ky, rqtn_ky, dt_of_acceptance 
          from RCTG.MQT_RECR_ACT_FCT 
         where off_stat in ('Accepted','Hired - DO NOT USE')) T1,
       STAGE_RCTG.SP_JOB_UTIL3 PSJ
      where 
       t1.cand_ky = raf.cand_ky AND
       t1.rqtn_ky = raf.rqtn_ky AND
       t1.cand_ky = psj.cand_ky AND
       PSJ.EFFDT = (select min(spj1.EFFDT) 
                             from STAGE_RCTG.SP_JOB spj1
                            where spj1.person_id = psj.person_id
                              and spj1.EFFDT >= (t1.dt_of_acceptance - 30 days) )
       fetch first 1 rows only)
where raf.off_stat in ('Accepted','Hired - DO NOT USE')
and raf.cand_ky in (select distinct t1.cand_ky 
                 from rctg.mqt_recr_act_fct t1, rctg.whs_cand_dim t2
                 where t1.cand_ky = t2.cand_ky
                   and t2.src_sys_id = 'D'
                   and t1.off_stat in ('Accepted','Hired - DO NOT USE'));--
                   
update rctg.mqt_recr_act_fct raf
set PS_STRT_DT = (
      select 
       min(PSJ.EFFDT) AS PS_STRT_DT
      from 
       (select cand_ky, rqtn_ky, dt_of_acceptance
          from RCTG.MQT_RECR_ACT_FCT 
         where off_stat in ('Accepted','Hired - DO NOT USE')) T1,
       STAGE_RCTG.SP_JOB_UTIL2 PSJ
      where 
       t1.cand_ky = raf.cand_ky AND
       t1.rqtn_ky = raf.rqtn_ky AND
       t1.cand_ky = psj.cand_ky AND
       PSJ.EFFDT = (select min(spj1.EFFDT) 
                             from STAGE_RCTG.SP_JOB spj1
                            where spj1.person_id = psj.person_id
                              and spj1.EFFDT >= (t1.dt_of_acceptance - 30 days) )
       fetch first 1 rows only)
where raf.off_stat in ('Accepted','Hired - DO NOT USE')
and raf.cand_ky in (select distinct t1.cand_ky 
                 from rctg.mqt_recr_act_fct t1, rctg.whs_cand_dim t2
                 where t1.cand_ky = t2.cand_ky
                   and t2.src_sys_id = 'U'
                   and t1.off_stat in ('Accepted','Hired - DO NOT USE'));--
                   
/* All other updates */                   
FOR myrow as (select mqt.CAND_KY as CANDKY, mqt.RQTN_KY as RQTNKY, mqt.OFF_STAT as OFFST, mqt.INTRVW_STAT as INTST, mqt.INTRVW_TP as INTTP, 
			  candim.SRC_SYS_ID as SYSID
			  from rctg.mqt_recr_act_fct mqt, rctg.WHS_CAND_DIM candim
			  where candim.CAND_KY = mqt.CAND_KY
			  and ((MQT.OFF_STAT is not null) OR
			       (MQT.INTRVW_STAT = 'Interest' and candim.SRC_SYS_ID = 'U' and MQT.INTRVW_TP = 'Hiring Manager Interview')
			      )
			 )
			  
DO
		SET V_CAND_KY = myrow.CANDKY;--
		SET V_RQTN_KY = myrow.RQTNKY;--
		
/* BC_RSLT, BC_DT, BC_PKG, DRVR_CHK_STAT, DRVR_CHK_DT, PROF_LIC_CERT_RSLT, PROF_LIC_CERT_DT Updates */
		UPDATE rctg.mqt_recr_act_fct mqt
		set (BC_RSLT, BC_DT, BC_PKG, DRVR_CHK_STAT, DRVR_CHK_DT, PROF_LIC_CERT_RSLT, PROF_LIC_CERT_DT) =
		  (select 
		 raf.recr_act_rslt_1 as BC_RSLT,
		 date(raf.recr_act_cmpl_dt) as BC_DT,
		 raf.std_ordr_tp as BC_PKG,
		 raf.driver_check_stat AS DRVR_CHK_STAT,
		 raf.driver_check_dt AS DRVR_CHK_DT,
		 raf.prof_lic_cert_stat AS PROF_LIC_CERT_RSLT,
		 raf.prof_lic_cert_dt
		   from 
		 rctg.whs_recr_act_fct raf, 
		 rctg.whs_recr_evnt_dim red
		   where
		    raf.rqtn_ky = mqt.rqtn_ky AND
		    raf.cand_ky = mqt.cand_ky AND    
		    raf.recr_evnt_ky = red.recr_evnt_ky AND
		    red.recr_evnt_nm = 'Prehire' AND
		    red.recr_evnt_tp_cd = 'BC' AND
		    raf.recr_act_dt = (select max(raf1.recr_act_dt)
		                             from rctg.whs_recr_act_fct raf1,
		                                  rctg.whs_recr_evnt_dim red1
		                            where raf1.recr_evnt_ky = red1.recr_evnt_ky
		                              and raf1.cand_ky = raf.cand_ky
		                              and raf1.rqtn_ky = raf.rqtn_ky
		                              and raf1.recr_evnt_ky = raf.recr_evnt_ky
		                              and red1.recr_evnt_nm = 'Prehire'
		                              and red1.recr_evnt_tp_cd = 'BC'
		                              and raf.load_cyc = (select max(raf2.load_cyc)
		                                                    from rctg.whs_recr_act_fct raf2,
		                                                         rctg.whs_recr_evnt_dim red2
		                                                   where raf2.recr_evnt_ky = red2.recr_evnt_ky
		                                                     and raf2.cand_ky = raf1.cand_ky
		                                                     and raf2.rqtn_ky = raf1.rqtn_ky
		                                                     and red2.recr_evnt_nm = 'Prehire'
		                                                     and red2.recr_evnt_tp_cd = 'BC'))
		                              fetch first 1 rows only )
		  where cand_ky = V_CAND_KY
		    and rqtn_ky = V_RQTN_KY;--
	    
/* DRG_TST_RSLT, DRS_TST_DT Updates */	    
		UPDATE rctg.mqt_recr_act_fct mqt
		set (DRG_TST_RSLT, DRG_TST_DT) =  
		(select 
		 raf.recr_act_rslt_1 as DRG_TST_RSLT,
		 DATE(raf.recr_act_cmpl_dt) as DRG_TST_DT
		   from 
		 rctg.whs_recr_act_fct raf, 
		 rctg.whs_recr_evnt_dim red
		   where
		    raf.rqtn_ky = mqt.rqtn_ky AND
		    raf.cand_ky = mqt.cand_ky AND
		 raf.recr_evnt_ky = red.recr_evnt_ky AND
		 red.recr_evnt_nm = 'Prehire' AND
		 red.recr_evnt_tp_cd = 'DT' AND
		    raf.recr_act_dt = (select max(raf1.recr_act_dt)
		                             from rctg.whs_recr_act_fct raf1,
		                                  rctg.whs_recr_evnt_dim red1
		                            where raf1.recr_evnt_ky = red1.recr_evnt_ky
		                              and raf1.cand_ky = raf.cand_ky
		                              and raf1.rqtn_ky = raf.rqtn_ky
		                              and raf1.recr_evnt_ky = raf.recr_evnt_ky
		                              and red1.recr_evnt_nm = 'Prehire'
		                              and red1.recr_evnt_tp_cd = 'DT'
		                              and raf.load_cyc = (select max(raf2.load_cyc)
		                                                    from rctg.whs_recr_act_fct raf2,
		                                                         rctg.whs_recr_evnt_dim red2
		                                                   where raf2.recr_evnt_ky = red2.recr_evnt_ky
		                                                     and raf2.cand_ky = raf1.cand_ky
		                                                     and raf2.rqtn_ky = raf1.rqtn_ky
		                                                     and red2.recr_evnt_nm = 'Prehire'
		                                                     and red2.recr_evnt_tp_cd = 'DT'))
		                              fetch first 1 rows only)
		  where cand_ky = V_CAND_KY
		    and rqtn_ky = V_RQTN_KY;	    --
		    
/* PS_TERM_DT Update */		    
		UPDATE RCTG.MQT_RECR_ACT_FCT mqt
		SET PS_TERM_DT = 
		  (
		   select 
		/*    distinct 
		    t1.cand_ky, 
		    t1.rqtn_ky, 
		    t3.companyemployeeid2 as PERSON_ID, 
		    t1.job_bid_date, 
		    t1.dt_of_acceptance, 
		*/    T4.EFFDT as PS_TERM_DT
		/*    t2.mii_emplid, 
		    off_stat */
		   from rctg.mqt_recr_act_fct t1,
		    rctg.whs_cand_dim t2,
		    stage_rctg.summ_emp t3,
		    stage_rctg.sp_job_UTIl1 t4
		   where 
		    t1.cand_ky = mqt.cand_ky AND
		    t1.rqtn_ky = mqt.rqtn_ky AND
		    t1.cand_ky = t2.cand_ky AND 
		    t1.off_stat in ('Pending','Accepted') AND
		    t2.src_sys_id = 'D' AND
		    t2.cand_id = t3.candidateid AND
		    t3.companyemployeeid2 = t4.person_id AND
		    t1.dt_of_acceptance <= t4.effdt AND
		    t4.effdt = (select 
		                 max(effdt) 
		                from 
		                 STAGE_RCTG.SP_JOB SPJ
		                where 
		                 SPJ.PERSON_ID = T4.PERSON_ID AND
		                 empl_rcd_num = 0 AND
		                 t1.dt_of_acceptance <= SPJ.effdt
		               )
		    union        
		   
		   select 
		/*    distinct 
		    t1.cand_ky, 
		    t1.rqtn_ky, 
		    pid.person_id as PERSON_ID, 
		    t1.job_bid_date, 
		    t1.dt_of_acceptance, 
		*/    T4.EFFDT as PS_TERM_DT
		/*    t2.mii_emplid, 
		    off_stat*/
		   from 
		    rctg.mqt_recr_act_fct t1,
		    rctg.whs_cand_dim t2,
		    stage_rctg.sp_person_id_tbl pid,
		    stage_rctg.SP_PERSONAL_DATA_CURR_V spp,
		    stage_rctg.SP_JOB_UTIl1 t4
		   where 
		    t1.cand_ky = mqt.cand_ky AND
		    t1.rqtn_ky = mqt.rqtn_ky AND
		    t1.off_stat in ('Pending','Accepted') AND 
		    t1.cand_ky = t2.cand_ky AND 
		    t2.src_sys_id = 'U' AND
		    t2.cand_ssn = pid.id_value AND 
		    pid.id_type = 'PR' AND 
		    pid.person_id = spp.person_id AND 
		    spp.person_type = 'EE' AND 
		    pid.person_id = t4.person_id AND 
		    t1.dt_of_acceptance <= t4.effdt AND 
		    t4.effdt = (select 
		                 min(effdt) 
		                from 
		                 STAGE_RCTG.SP_JOB SPJ
		                where 
		                 SPJ.PERSON_ID = T4.PERSON_ID AND
		                 SPJ.EMPL_RCD_NUM = 0 AND
		                 t1.dt_of_acceptance <= SPJ.effdt
		               )      
		                fetch first 1 rows only         
		    )
		  where cand_ky = V_CAND_KY
		    and rqtn_ky = V_RQTN_KY;--
		    
/* VISA_STATUS Update */		    
		UPDATE RCTG.MQT_RECR_ACT_FCT mqt
		SET VISA_STATUS = 
		    (
		     
		     select 
		/*      t1.cand_ky, 
		      t1.rqtn_ky, 
		      t3.companyemployeeid2 as person_id, 
		*/      t4.VISA_PERMIT_TYPE AS VISA_STATUS
		     from rctg.mqt_recr_act_fct t1,
		      rctg.whs_cand_dim t2,
		      stage_rctg.summ_emp t3,
		      stage_rctg.sp_visa_pmt_data t4
		     where 
		      t1.cand_ky = mqt.cand_ky AND
		      t1.rqtn_ky = mqt.rqtn_ky AND
		      t1.cand_ky = t2.cand_ky AND
		      t1.off_stat in ('Accepted','Pending') AND
		      t2.src_sys_id = 'D' AND 
		      t2.cand_id = t3.candidateid AND
		      t3.companyemployeeid2 = t4.person_id AND
		      t1.job_bid_date between t4.DT_ISSUED and t4.EXPIRATN_DT AND
		      t4.effdt = (select 
		                   max(effdt) 
		                  from 
		                   STAGE_RCTG.SP_VISA_PMT_DATA V
		                  where 
		                   V.PERSON_ID = T4.PERSON_ID AND
		                   t1.job_bid_date between v.DT_ISSUED and V.EXPIRATN_DT
		                 )
		     union      
		     
		     select 
		/*      t1.cand_ky, 
		      t1.rqtn_ky, 
		      pid.person_id as person_id, 
		*/      t4.VISA_PERMIT_TYPE AS VISA_STATUS
		     from 
		      rctg.mqt_recr_act_fct t1,
		      rctg.whs_cand_dim t2,
		      stage_rctg.sp_person_id_tbl pid,
		      stage_rctg.sp_personal_data_curr_v spp,
		      stage_rctg.sp_visa_pmt_data t4
		     where 
		      t1.cand_ky = mqt.cand_ky AND
		      t1.rqtn_ky = mqt.rqtn_ky AND
		      t1.cand_ky = t2.cand_ky AND 
		      t1.off_stat in ('Accepted','Pending') AND
		      t2.src_sys_id = 'U' AND
		      t2.cand_ssn = pid.id_value AND
		      pid.id_type = 'PR' AND
		      pid.person_id = spp.person_id AND
		      spp.person_type = 'EE' AND
		      pid.person_id = t4.person_id AND
		      t1.job_bid_date between t4.DT_ISSUED and t4.EXPIRATN_DT AND
		      t4.effdt = (select 
		                   max(effdt) 
		                  from 
		                   STAGE_RCTG.SP_VISA_PMT_DATA V
		                  where 
		                   V.PERSON_ID = T4.PERSON_ID AND
		                   t1.job_bid_date between v.DT_ISSUED and V.EXPIRATN_DT
		                 )
		                  fetch first 1 rows only
		     )
		  where cand_ky = V_CAND_KY
		    and rqtn_ky = V_RQTN_KY;		    		    --
		    
/* DRG_TST_REQ Updates */		    
		UPDATE RCTG.MQT_RECR_ACT_FCT                                          
		SET DRG_TST_REQ ='N' WHERE app_tp='Internal' and cand_ky = V_CAND_KY and rqtn_ky = V_RQTN_KY;--
		
		UPDATE RCTG.MQT_RECR_ACT_FCT raf
		SET DRG_TST_REQ = 
		     (SELECT 
		/*       T1.RQTN_NR, 
		       T1.RQTN_KY, 
		       T1.RQTN_FLAT_ORG_ID_4, 
		       RQTN_FLAT_ORG_NM_4, 
		*/       COALESCE(T2.MI_DRUG_TESTING,'N') AS DRG_TST_REQ
		      FROM 
		     RCTG.MQT_RQTN_UTIL t1,
		       (select distinct LOCATION,MI_DRUG_TESTING from STAGE_RCTG.PS_LOCATION_TBL where MI_DRUG_TESTING is not null ) T2
		      WHERE 
		       t1.rqtn_ky =  raf.rqtn_ky AND 
		       t1.location = T2.LOCATION
		     fetch first 1 rows only) where app_tp='External' and cand_ky = V_CAND_KY and rqtn_ky = V_RQTN_KY;--
/* RAPID_HIRE Update */     
			update RCTG.MQT_RECR_ACT_FCT mqt
			SET RAPID_HIRE = (SELECT 
			                   CASE WHEN 
			                             DISPOSITION_REASON like '%Rapid Hire%'  OR
			                             DRUG_RSLT like '%Rapid Hire%'  OR 
			                             REF_CHK_RSLTS like '%Rapid Hire%'  OR
			                             CKR_REF_CHK_RSLT like '%Rapid Hire%' 
			                        THEN 'Y' 
			                        ELSE 'N' END AS Rapid_Hire
			                  FROM 
			                   RCTG.MQT_RECR_ACT_FCT MQT1
			                  WHERE MQT1.RQTN_KY = mqt.rqtn_ky
			                    AND MQT1.CAND_KY = mqt.cand_ky
			                    fetch first 1 rows only 
			                 )
			                 where cand_ky = V_CAND_KY and rqtn_ky = V_RQTN_KY;--
			                 
/* PRE_EMP_REQ_MET Updates */			                 
			update RCTG.MQT_RECR_ACT_FCT mqt
			SET PRE_EMP_REQ_MET =           
			(           
			SELECT 
			CASE 
			 
			 WHEN PS_STRT_DT IS NULL 
			    THEN ''
			 WHEN 
			   BC_REQ in (V_BCREQ6, V_BCREQ1) AND
			   ((DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL 
			     AND PS_STRT_DT IS NOT NULL 
			     AND DRG_TST_DT < PS_STRT_DT) OR
			    (DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL 
			     AND PS_STRT_DT IS NULL) OR
			    (DRG_TST_REQ = 'N')
			   )
			   THEN 'Y'
			 WHEN 
			   posstr(BC_REQ, V_BCREQ5) > 0  AND
			   ((BC_DT IS NOT NULL 
			     AND PS_STRT_DT IS NOT NULL
			     AND BC_DT < PS_STRT_DT) 
			    OR
			    (BC_DT IS NULL)
			   ) AND
			   ((DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND DRG_TST_DT < PS_STRT_DT) OR
			    (DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL) OR
			    (DRG_TST_REQ = 'N')
			   )
			   THEN 'Y'
			 WHEN
			   BC_REQ = V_BCREQ2 AND
			   BC_PKG in (V_BC_PKG2, V_BC_PKG3) AND
			   ((BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND BC_DT < PS_STRT_DT) OR
			    (BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL)) AND
			   ((DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND DRG_TST_DT < PS_STRT_DT) OR
			    (DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL) OR
			    (DRG_TST_REQ = 'N')
			   )
			   THEN 'Y'
			 WHEN
			   BC_REQ = V_BCREQ3 AND
			   BC_PKG in (
			   V_BC_PKG2, 
			   V_BC_PKG3, 
			   V_BC_PKG4, 
			   V_BC_PKG5, 
			   V_BC_PKG6, 
			   V_BC_PKG7) AND
			   ((BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND BC_DT < PS_STRT_DT) OR
			    (BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL)) AND
			   ((DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND DRG_TST_DT < PS_STRT_DT) OR
			    (DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL) OR
			    (DRG_TST_REQ = 'N')
			   )
			   THEN 'Y'
			 WHEN
			   BC_REQ = V_BCREQ4 AND
			   BC_PKG in (
			   V_BC_PKG1, 
			   V_BC_PKG2, 
			   V_BC_PKG3, 
			   V_BC_PKG4, 
			   V_BC_PKG5, 
			   V_BC_PKG6, 
			   V_BC_PKG7) AND
			   BC_DT < PS_STRT_DT AND
			   ((BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND BC_DT < PS_STRT_DT) OR
			    (BC_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL)) AND
			   ((DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NOT NULL
			     AND DRG_TST_DT < PS_STRT_DT) OR
			    (DRG_TST_REQ = 'Y' 
			     AND DRG_TST_DT IS NOT NULL
			     AND PS_STRT_DT IS NULL) OR
			    (DRG_TST_REQ = 'N')
			   )
			   THEN 'Y'
			    ELSE 'N'
			    END AS PRE_EMP_REQ_MET
			FROM 
			  RCTG.MQT_RECR_ACT_FCT MQT1
			  WHERE MQT1.CAND_KY = mqt.cand_ky
			    AND MQT1.RQTN_KY = mqt.rqtn_ky
			    
			    fetch first 1 rows only 
			    )
			    where cand_ky = V_CAND_KY and rqtn_ky = V_RQTN_KY;                 --
                    
			/* IF BC and DT not Required, the set to 'Y' */                   
			update RCTG.MQT_RECR_ACT_FCT
			SET PRE_EMP_REQ_MET = 'Y'           
			where BC_REQ in (V_BCREQ6, V_BCREQ1) 
			    AND DRG_TST_REQ = 'N' AND cand_ky = V_CAND_KY and rqtn_ky = V_RQTN_KY;  --
			                            		
END FOR;--
/*&&&&&&&&&&&&&&&&&&&&&*/
END;

