CREATE PROCEDURE RCTG.SP_WHS_RECR_ACT_FCT
 (IN PARM1 INTEGER
 ) 
  LANGUAGE SQL
  NOT DETERMINISTIC
  CALLED ON NULL INPUT
  MODIFIES SQL DATA
  INHERIT SPECIAL REGISTERS
  Begin

delete from rctg.mqt_recr_act_fct;--

INSERT INTO RCTG.MQT_RECR_ACT_FCT (
       RQTN_KY,
           CAND_KY, 
           CAND_GNRC_SRC, 
           CAND_SPC_SRC, 
           RFRD_BY_EMAL, 
           RFRD_BY_EMPLID,
           RFRD_BY_NM, 
           DT_OF_ACCEPTANCE, 
           off_extn_dt,
           DT_OF_STRT, 
           OFF_STAT, 
           OFF_STAT_DT, 
           Days_to_Accept_Offer, 
           RLCTN_PKG,
           CST_PER_HIR, 
           BGC_RSLT, 
           bgc_compl_dt, 
           DRUG_RSLT, 
           drug_test_compl_dt, 
           Disposition_Reason, 
           Screen_Date,
           Screen_Status, 
           Screen_Type, 
           Ref_Chk_Rslts, 
/*         Date_Ref_Chk_Compl, */
           PRIM_ASSMT_DT, 
           PRIM_ASSMT_RSLT,
           PRIM_ASSMT_TP, 
           prim_assmt_compl_dt, 
           SEC_ASSMT_DT,
           SEC_ASSMT_RSLT, 
           SEC_ASSMT_TP, 
           sec_assmt_compl_dt, 
           event_type, 
           event_stat, 
           event_stat_date, 
           INTRVW_DT,
           INTRVW_STAT, 
           INTRVW_STAT_DT, 
           INTRVW_TP, 
           LOAD_CYC,
           EXCL_IND,
           STATUS_FLAG,
           STATUS_FLAG_DT,
           CKR_REF_CHK_RSLT,
           PS_REF_CHK_RSLT)
SELECT DISTINCT T0.RQTN_KY, 
           T0.CAND_KY, 
           case when (T2.CAND_GNRC_SRC is null) then 'NA' else T2.CAND_GNRC_SRC end as CAND_GNRC_SRC, 
           case when (T2. CAND_SPC_SRC is null) then 'NA' else T2.CAND_SPC_SRC end as CAND_SPC_SRC, 
           T2.RFRD_BY_EMAL, 
           T2.RFRD_BY_EMPL_ID,
           T2.RFRD_BY_NM, 
           T3.DT_OF_ACCEPTANCE, 
           T3.off_extn_dt,
           T3.DT_OF_STRT, 
           T3.OFF_STAT, 
           T3.OFF_STAT_DT, 
           T3.Days_to_Accept_Offer, 
           T3.RLCTN_PKG,
           T3.CST_PER_HIR, 
           T4.BGC_RSLT, 
           T4.bgc_compl_dt, 
           T5.DRUG_RSLT, 
           T5.drug_test_compl_dt, 
           T6.Disposition_Reason, 
           T7.Screen_Date,
           T7.Screen_Status, 
           T7.Screen_Type, 
           T0.SRC_SYS_ID,
/*         T8.Ref_Chk_Rslts, 
           t8.Date_Ref_Chk_Compl, */
           t9.PRIM_ASSMT_DT, 
           t9.PRIM_ASSMT_RSLT,
           t9.PRIM_ASSMT_TP, 
           t9.prim_assmt_compl_dt, 
           t10.SEC_ASSMT_DT,
           t10.SEC_ASSMT_RSLT, 
           t10.SEC_ASSMT_TP, 
           t10.sec_assmt_compl_dt, 
           t11.event_type, 
           t11.event_stat, 
           t11.event_stat_date, 
           T12.INTRVW_DT,
           T12.INTRVW_STAT, 
           T12.INTRVW_STAT_DT, 
           T12.INTRVW_TP, 
           T13.LOAD_CYC ,
           case when (T14.EXCL_IND IS NULL) then 'Y' else T14.EXCL_IND end as EXCL_IND,
           case when (t15.cand_ky_15 is not null) then 'D' else NULL end as STATUS_FLAG,
           case when (t15.cand_ky_15 is not null) then evnt_dt else NULL end as STATUS_FLAG_DT,
           T16.CKR_REF_CHK_RSLT,
           T17.PS_REF_CHK_RSLT
  From RCTG.WHS_RECR_ACT_FCT T0 
  
  Left Outer Join 
  ( SELECT distinct A. RQTN_KY AS RQTN_KY_2, 
           A.CAND_KY As CAND_KY_2, 
           D.RECR_EVNT_TP AS CAND_GNRC_SRC, 
           B.SPC_SRC AS CAND_SPC_SRC, 
           B.RFRD_BY_EMAL as RFRD_BY_EMAL, 
           B.RFRD_BY_EMPL_ID as RFRD_BY_EMPL_ID, 
           B.RFRD_BY_NM as RFRD_BY_NM 
      from RCTG.WHS_RECR_ACT_FCT A, 
           RCTG.WHS_RECR_ACT_FCT B, 
           RCTG.WHS_RECR_EVNT_DIM C, 
           RCTG.WHS_RECR_EVNT_DIM D 
     where A.CAND_SRC_ID = B.RECR_ACT_KY 
       and A.RECR_EVNT_KY = C.RECR_EVNT_KY 
       and B.RECR_EVNT_KY = D.RECR_EVNT_KY
       and C.RECR_EVNT_NM = 'Job Bid'
       and D.RECR_EVNT_NM = 'Candidate Source') AS T2 
  on T0.RQTN_KY = T2 .RQTN_KY_2 and T0.CAND_KY = T2. CAND_KY_2 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_3, 
           CAND_KY As CAND_KY_3, 
           Date(DT_OF_ACPT) as DT_OF_ACCEPTANCE, 
           Date( RECR_ACT_DT) as off_extn_dt, 
           DATE(DT_OF_STRT ) AS DT_OF_STRT, 
           Date( RECR_ACT_STAT_DT) as OFF_STAT_DT, 
           RECR_ACT_STAT as OFF_STAT, 
           (Date( DT_OF_ACPT)- Date(RECR_ACT_DT )) AS Days_to_Accept_Offer, 
           RLCTN_PKG, 
           CST_PER_HIR 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and A.CURR_EVNT_FL = 'Y' 
       and B.RECR_EVNT_NM = 'Offer') AS T3 
  on T0.RQTN_KY = T3. RQTN_KY_3 and T0.CAND_KY = T3.CAND_KY_3 
  
  Left Outer Join 
  ( SELECT RQTN_KY as RQTN_KY_4,
           CAND_KY As CAND_KY_4, 
           RECR_ACT_RSLT_1 AS BGC_RSLT, 
           date( RECR_ACT_CMPL_DT) as bgc_compl_dt,
           Date( RECR_ACT_INIT_DT ) AS BGC_INIT_DT 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B. RECR_EVNT_KY 
       and B.RECR_EVNT_TP = 'Background Check' 
       and A.RECR_ACT_KY = ( select max(P.RECR_ACT_KY) 
                               from RCTG.WHS_RECR_ACT_FCT as P, 
                                    RCTG.WHS_RECR_EVNT_DIM as Q 
                              where Q.RECR_EVNT_TP = 'Background Check' 
                                and P.CAND_KY = A.CAND_KY 
                                and P.RQTN_KY = A.RQTN_KY 
                                and P.RECR_EVNT_KY = Q.RECR_EVNT_KY 
                                and P.RECR_ACT_DT = ( select max(X.RECR_ACT_DT) 
                                                        from RCTG.WHS_RECR_ACT_FCT as X, 
                                                             RCTG.WHS_RECR_EVNT_DIM as Y 
                                                       where Y.RECR_EVNT_TP = 'Background Check' 
                                                         and X.RECR_EVNT_KY = Y. RECR_EVNT_KY 
                                                         and X.RQTN_KY = P.RQTN_KY 
                                                         and X.CAND_KY = P.CAND_KY))) AS T4 
  on T0.CAND_KY = T4.CAND_KY_4 and T0.RQTN_KY = T4.RQTN_KY_4 
  
  Left Outer Join 
  ( SELECT RQTN_KY as RQTN_KY_5, 
           CAND_KY As CAND_KY_5, 
           RECR_ACT_RSLT_1 AS DRUG_RSLT, 
           date( RECR_ACT_CMPL_DT) as drug_test_compl_dt 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and B.RECR_EVNT_TP = 'Drug Test' 
       and A.RECR_ACT_KY = ( select max(P.RECR_ACT_KY) 
                               from RCTG.WHS_RECR_ACT_FCT as P, 
                                    RCTG.WHS_RECR_EVNT_DIM as Q 
                              where Q.RECR_EVNT_TP = 'Drug Test' 
                                and P.CAND_KY =A.CAND_KY 
                                and P.RQTN_KY=A.RQTN_KY 
                                and P.RECR_EVNT_KY = Q.RECR_EVNT_KY 
                                and P.RECR_ACT_DT = ( select max(X.RECR_ACT_DT) 
                                                        from RCTG.WHS_RECR_ACT_FCT as X, 
                                                             RCTG.WHS_RECR_EVNT_DIM as Y 
                                                       where Y.RECR_EVNT_TP = 'Drug Test' 
                                                         and X.RECR_EVNT_KY = Y.RECR_EVNT_KY 
                                                         and X.RQTN_KY = P.RQTN_KY 
                                                         and X.CAND_KY = P.CAND_KY))) AS T5 
  on T0.CAND_KY = T5. CAND_KY_5 and T0.RQTN_KY = T5.RQTN_KY_5 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_6, 
           CAND_KY As CAND_KY_6, 
           RECR_EVNT_NM as LAST_EVNT, 
           RECR_ACT_STAT_RSN as Disposition_Reason 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and A.RQTN_KY IS NOT NULL
       and A.CAND_KY IS NOT NULL
       and A.CURR_FL = 'Y') AS T6 
  on T0.RQTN_KY = T6.RQTN_KY_6 and T0. CAND_KY = T6.CAND_KY_6 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_3, 
           CAND_KY As CAND_KY_3, 
           Date( RECR_ACT_DT) as Screen_Date, 
           RECR_ACT_STAT as Screen_Status, 
           recr_evnt_tp as Screen_Type 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and A.CURR_EVNT_FL = 'Y' 
       and B. RECR_EVNT_NM = 'Screen') AS T7 
  on T0.RQTN_KY = T7. RQTN_KY_3 and T0.CAND_KY = T7.CAND_KY_3 
  /*
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_3, 
           CAND_KY As CAND_KY_3, 
           RECR_ACT_STAT as Ref_Chk_Rslts, 
           case when recr_act_stat <> 'Pending' then date( RECR_ACT_DT) end as Date_Ref_Chk_Compl 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and A.CURR_EVNT_FL = 'Y' 
       and B.RECR_EVNT_NM = 'Reference Check') AS T8 
  on T0.CAND_KY = T8.CAND_KY_3 
  */
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_1, 
           CAND_KY As CAND_KY_1, 
           Date(RECR_ACT_DT) AS Prim_assmt_dt, 
           RECR_ACT_RSLT_1 AS Prim_Assmt_rslt, 
           RECR_EVNT_TP as Prim_Assmt_tp, 
           Date(RECR_ACT_STAT_DT) as Prim_Assmt_Compl_dt 
      FROM RCTG.WHS_RECR_ACT_FCT T1, 
           RCTG.WHS_RECR_EVNT_DIM T2 
     WHERE T1.RECR_EVNT_KY = T2.RECR_EVNT_KY 
       AND T2.RECR_EVNT_NM = 'Test' 
       AND T2.RECR_EVNT_TP NOT IN ( 'Drug Test', 'Background Check') 
       AND T1.RECR_ACT_KY = ( SELECT MAX( RECR_ACT_KY) 
                                FROM RCTG.WHS_RECR_ACT_FCT T3, 
                                     RCTG.WHS_RECR_EVNT_DIM T4 
                               WHERE T3.RQTN_KY = T1.RQTN_KY 
                                 AND T3.CAND_KY = T1.CAND_KY 
                                 AND T3.RECR_EVNT_KY = T4.RECR_EVNT_KY 
                                 AND T4.RECR_EVNT_NM = 'Test' 
                                 AND T4.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check') 
                                 AND T3.RECR_ACT_DT = ( SELECT MAX(RECR_ACT_DT) 
                                                          FROM RCTG.WHS_RECR_ACT_FCT T5, 
                                                               RCTG.WHS_RECR_EVNT_DIM T6 
                                                         WHERE T5.RQTN_KY = T3.RQTN_KY 
                                                           AND T5.CAND_KY = T3.CAND_KY 
                                                           AND T5.RECR_EVNT_KY = T6.RECR_EVNT_KY 
                                                           AND T6.RECR_EVNT_NM = 'Test' 
                                                           AND T6.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check'))) ) AS T9 
  ON T0 .RQTN_KY = T9.RQTN_KY_1 AND T0.CAND_KY = T9.CAND_KY_1 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_1,            CAND_KY As CAND_KY_1, 
           Date(RECR_ACT_DT) AS Sec_assmt_dt, 
           RECR_ACT_RSLT_1 AS Sec_assmt_rslt, 
           recr_evnt_tp as Sec_assmt_tp, 
           date(recr_act_stat_dt) as Sec_assmt_compl_dt 
      FROM RCTG.WHS_RECR_ACT_FCT T1, 
           RCTG.WHS_RECR_EVNT_DIM T2 
     WHERE T1.RECR_EVNT_KY = T2. RECR_EVNT_KY 
       AND T2.RECR_EVNT_NM = 'Test' 
       AND T2.RECR_EVNT_TP NOT IN ( 'Drug Test', 'Background Check') 
       AND T1.RECR_ACT_KY = 
       ( SELECT MAX( RECR_ACT_KY) 
           FROM RCTG.WHS_RECR_ACT_FCT T3, 
                RCTG.WHS_RECR_EVNT_DIM T4 
          WHERE T3.RQTN_KY = T1.RQTN_KY 
            AND T3.CAND_KY = T1.CAND_KY 
            AND T3.RECR_EVNT_KY = T4.RECR_EVNT_KY 
            AND T4.RECR_EVNT_NM = 'Test' 
            AND T4.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check') 
            AND T3.RECR_ACT_KY <> 
            ( SELECT MAX(RECR_ACT_KY) 
                FROM RCTG.WHS_RECR_ACT_FCT T7, 
                     RCTG.WHS_RECR_EVNT_DIM T8 
               WHERE T7.RQTN_KY = T3.RQTN_KY 
                 AND T7.CAND_KY = T3.CAND_KY 
                 AND T7.RECR_EVNT_KY = T8.RECR_EVNT_KY 
                 AND T8.RECR_EVNT_NM = 'Test' 
                 AND T8.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check') 
                 AND T7.RECR_ACT_DT = 
                 ( SELECT MAX(RECR_ACT_DT) 
                     FROM RCTG.WHS_RECR_ACT_FCT T9, 
                          RCTG.WHS_RECR_EVNT_DIM T10 
                    WHERE T9.RQTN_KY = T7.RQTN_KY 
                      AND T9.CAND_KY = T7. CAND_KY 
                      AND T9.RECR_EVNT_KY = T10.RECR_EVNT_KY 
                      AND T10.RECR_EVNT_NM = 'Test' 
                      AND T10.RECR_EVNT_TP NOT IN ( 'Drug Test', 'Background Check'))) 
            AND T3.RECR_ACT_DT = 
                ( SELECT MAX( RECR_ACT_DT) 
                    FROM RCTG.WHS_RECR_ACT_FCT T5, 
                         RCTG.WHS_RECR_EVNT_DIM T6 
                   WHERE T5.RQTN_KY = T3.RQTN_KY 
                     AND T5.CAND_KY = T3.CAND_KY 
                     AND T5.RECR_EVNT_KY = T6.RECR_EVNT_KY 
                     AND T6.RECR_EVNT_NM = 'Test' 
                     AND T6.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check') 
                     AND T5. RECR_ACT_KY <> 
                     ( SELECT MAX(RECR_ACT_KY) 
                         FROM RCTG.WHS_RECR_ACT_FCT T7, 
                              RCTG.WHS_RECR_EVNT_DIM T8 
                        WHERE T7.RQTN_KY = T5.RQTN_KY 
                          AND T7.CAND_KY = T5.CAND_KY 
                          AND T7.RECR_EVNT_KY = T8.RECR_EVNT_KY 
                          AND T8.RECR_EVNT_NM = 'Test' 
                          AND T8.RECR_EVNT_TP NOT IN ('Drug Test', 'Background Check') 
                          AND T7.RECR_ACT_DT = 
                          ( SELECT MAX(RECR_ACT_DT ) 
                              FROM RCTG.WHS_RECR_ACT_FCT T9, 
                                   RCTG.WHS_RECR_EVNT_DIM T10 
                             WHERE T9.RQTN_KY = T7.RQTN_KY 
                               AND T9.CAND_KY = T7.CAND_KY 
                               AND T9.RECR_EVNT_KY = T10.RECR_EVNT_KY 
                               AND T10.RECR_EVNT_NM = 'Test' 
                               AND T10.RECR_EVNT_TP NOT IN ( 'Drug Test', 'Background Check'))))
           ) ) AS T10 
  ON T0.RQTN_KY = T10.RQTN_KY_1 AND T0.CAND_KY = T10.CAND_KY_1 
                                                                                                                 
  Left Outer Join 
  ( SELECT rqtn_ky as rqtn_ky_1,
           cand_ky as cand_ky_1, 
           recr_act_stat as event_stat, 
           DATE(RECR_ACT_STAT_DT) as event_stat_date, 
           recr_evnt_nm as event_type 
      from rctg.whs_recr_act_fct a, 
           rctg.whs_recr_evnt_dim b 
     where a.recr_evnt_ky = b.recr_evnt_ky 
       and a.rqtn_ky is not null
       and a.cand_ky is not null
       and a.CURR_FL = 'Y') AS T11 
  on T0.RQTN_KY = T11.RQTN_KY_1 and T0.CAND_KY = T11. CAND_KY_1 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_12, 
           CAND_KY As CAND_KY_12, 
           Date( RECR_ACT_DT) as INTRVW_DT,
           RECR_ACT_STAT as INTRVW_STAT, 
           Date( RECR_ACT_STAT_DT) as INTRVW_STAT_DT, 
           RECR_EVNT_TP as INTRVW_TP 
      FROM RCTG.WHS_RECR_ACT_FCT as A, 
           RCTG.WHS_RECR_EVNT_DIM as B 
     where A.RECR_EVNT_KY = B.RECR_EVNT_KY 
       and A.CURR_EVNT_FL = 'Y' 
       and B.RECR_EVNT_NM = 'Interview') AS T12 
  on T0.RQTN_KY = T12.RQTN_KY_12 and T0.CAND_KY = T12. CAND_KY_12 
  
  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_13, 
           CAND_KY As CAND_KY_13, 
           max( LOAD_CYC) as LOAD_CYC 
      FROM RCTG.WHS_RECR_ACT_FCT as A 
     WHERE A.RQTN_KY IS NOT NULL
       AND A.CAND_KY IS NOT NULL
      group by RQTN_KY, CAND_KY ) AS T13 
  on T0.RQTN_KY = T13.RQTN_KY_13 and T0.CAND_KY = T13. CAND_KY_13 
  
  Left Outer Join 
  ( SELECT DISTINCT T2.CAND_KY AS CAND_KY_14,
                   T2.RQTN_KY AS RQTN_KY_14, 
                   'N' as EXCL_IND
      FROM RCTG.WHS_RECR_ACT_FCT T2, RCTG.WHS_RQTN_DIM as T3
     WHERE T2.RQTN_KY = T3.RQTN_KY
           AND T2.CAND_KY IS NOT NULL
           AND NOT (T3.TP_OF_OPNG = 'Hourly/Non-Management'
                              AND T3.Ctry = 'United States'
                              AND Posstr(T3.Workgroup_String,'MI College Recruiting') = 0
                              AND Posstr(T3.Workgroup_String,'MVCI College Recruiting') = 0
                              AND T2.SRC_SYS_ID = 'D'
                             )
 ) AS T14
  on T0.RQTN_KY = T14.RQTN_KY_14 AND T0.CAND_KY = T14.CAND_KY_14

  Left Outer Join
  ( Select t2.cand_ky as cand_ky_15, 
           t3.rqtn_ky as rqtn_ky_15, 
           max(evnt_dt) evnt_dt
      from STAGE_RCTG.UNC_STD_WORKFLOW t1,
           RCTG.WHS_CAND_DIM T2,
           RCTG.WHS_RQTN_DIM T3
     where end_wkf_stat_cd = 'DEL'
       and t1.unc_apl_id = t2.cand_id
       and t1.rqtn_nr = t3.rqtn_nr
       group by t2.cand_ky, t3.rqtn_ky
   ) AS T15
   on T0.RQTN_KY = T15.RQTN_KY_15 AND T0.CAND_KY = T15.CAND_KY_15

  Left Outer Join 
  ( SELECT RQTN_KY AS RQTN_KY_1, 
           CAND_KY As CAND_KY_1, 
           RECR_ACT_STAT AS CKR_REF_CHK_RSLT
      FROM RCTG.WHS_RECR_ACT_FCT T1, 
           RCTG.WHS_RECR_EVNT_DIM T2 
     WHERE T1.RECR_EVNT_KY = T2.RECR_EVNT_KY 
       AND T2.RECR_EVNT_NM = 'Reference Check' 
       AND T2.RECR_EVNT_TP_CD = 'CKR') AS T16
  ON T0.RQTN_KY = T16.RQTN_KY_1 AND T0.CAND_KY = T16.CAND_KY_1 
  
  LEFT OUTER JOIN
  (select t2.cand_ky, t2.rqtn_ky, t4.mi_reference_check as ps_ref_chk_rslt
    from rctg.whs_cand_dim t1,
       rctg.whs_recr_act_fct t2,
       rctg.whs_rqtn_dim t3,
       (Select distinct t2.national_id, t1.rqtn_nr, t5.DESCR as MI_REFERENCE_CHECK
          from STAGE_RCTG.PS_MI_ADJUDICATION t1
               inner join (select applid, national_id from STAGE_RCTG.PS_APP_NID 
                            where primary_nid = 'Y' and ltrim(rtrim(national_id)) <> '') t2 on t1.applid = t2.applid
               left outer join (select FIELDVALUE, DESCR from STAGE_RCTG.SP_CODE_DESCR_TBL WHERE FIELDNAME = 'MI_REFERENCE_CHECK'
                           and LANGUAGE_CD = 'ENG') t5 on t1.MI_REFERENCE_CHECK = t5.FIELDVALUE
           where t1.rqtn_nr > 0
           and t1.mi_reference_check <> ''
           and t1.app_dt = 
               (select max(app_dt) from STAGE_RCTG.PS_MI_ADJUDICATION t7
                 where t7.applid = t1.applid
                   and t7.rqtn_nr = t1.rqtn_nr)) t4
 where t2.cand_ky = t1.cand_ky
   and t2.rqtn_ky = t3.rqtn_ky
   and t1.cand_ssn = t4.national_id
   and t3.rqtn_nr = t4.rqtn_nr) as t17
  ON T0.RQTN_KY = T17.RQTN_KY AND T0.CAND_KY = T17.CAND_KY
    
where T0.CURR_FL = 'Y' and t0.rqtn_ky is not null and t0.cand_ky is not null;--

