update rctg.mqt_recr_act_fct raf
set (jobbid_stat, job_bid_date, app_tp, pers_grant_excep, exception_granted, aplct_indict) = 
(
select  t1.recr_act_stat jobbid_stat, 
                date(recr_act_dt) job_bid_date , 
                left(t2.recr_evnt_tp,8) app_tp, 
                pers_grant_excep, 
                exception_granted, 
           case 
                                    when left(t2.recr_evnt_tp,8) = 'External' and t1.src_sys_id = 'U' then 'Y'
                               when RECR_ACT_STAT in ( 'External - Consider Further', 'Internal - Eligibility Verified', 'Internal - Exception Verified') 
                                         AND NOT (T3.TP_OF_OPNG = 'Hourly/Non-Management' 
                                                             AND T3.Ctry = 'United States' 
                                                             AND Posstr(T3.Workgroup_String, 'MI College Recruiting' )=0 
                                                             AND Posstr(T3.Workgroup_String,'MVCI College Recruiting')= 0
                                                             AND T1.SRC_SYS_ID = 'D'
                                                            )
                                then 'Y' else 'N' end as applct_indct
                          from rctg.whs_recr_act_fct t1, rctg.whs_recr_evnt_dim t2, rctg.whs_rqtn_dim t3
                         where t1.recr_evnt_ky = t2.recr_evnt_ky 
                           and t1.cand_ky = raf.cand_ky
                           and t1.rqtn_ky = raf.rqtn_ky
                           and t2.recr_evnt_nm = 'Job Bid'
                           and t1.rqtn_ky = t3.rqtn_ky
                          order by 
                              t1.cand_ky, 
                              t1.rqtn_ky,
                                    case
                               when recr_act_stat in ('External - Consider Further', 'Internal - Eligibility Verified', 'Internal - Exception Verified') 
                               then 1
                               when recr_act_stat = 'Pending' 
                               then 2
                               when recr_act_stat not in ('External - Consider Further', 'Internal - Eligibility Verified', 'Internal - Exception Verified','Pending') 
                               then 3
                               else 4
                               end, 
                               recr_act_stat_dt desc, 
                               recr_act_fct_ky desc
                               fetch first 1 rows only
);                                                              --
  
