Create Procedure RCTG.TBNT2 (IN vBeginDate DATE, vEndDate DATE, v_DateParm
VARCHAR(3))  
	Dynamic Result Sets 0 
	Modifies SQL Data     
	Language SQL 
P1: BEGIN
    
    DECLARE cursor1 CURSOR WITH RETURN FOR
select distinct cand_ky, rqtn_ky, vBeginDate as begindate, vEndDate as enddate
 from (
SELECT   date(whs.recr_act_stat_dt) as recr_act_stat_dt, whs.cand_ky, whs.rqtn_ky
FROM     RCTG.WHS_RECR_ACT_FCT WHS, 
         RCTG.WHS_RECR_EVNT_DIM WHS1,
         RCTG.WHS_RQTN_DIM T1
WHERE    WHS.RECR_EVNT_KY=WHS1.RECR_EVNT_KY 
AND      DATE(WHS.RECR_ACT_STAT_DT)  between vBeginDate and vEndDate
AND      ( (WHS1.RECR_EVNT_NM = 'Job Bid' AND WHS.RECR_ACT_STAT = 'External - Remove From Consideration') OR
           (WHS1.RECR_EVNT_NM = 'Screen' AND WHS.RECR_ACT_STAT = 'No Interest') OR
           (WHS1.RECR_EVNT_NM = 'Interview' AND WHS.RECR_ACT_STAT = 'No Interest') OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS1.RECR_EVNT_TP = 'Drug Test' AND WHS.RECR_ACT_STAT in  ('Fail - Drug test','Drug check failed')) OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS1.RECR_EVNT_TP = 'Background Check' AND WHS.RECR_ACT_STAT in ('Fail - Background check','Fail -  Background check','Background check failed')) OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS.RECR_ACT_STAT = 'Did Not Get Through Qualifier - QSP')
          )
AND WHS.SRC_SYS_ID = 'D'
AND WHS.RQTN_KY = T1.RQTN_KY
AND T1.RQTN_CURR_STAT in ('Open','Hold','ReOpen','Offer','Canceled')

union

select date(recr_act_stat_dt) recr_act_stat_dt, cand_ky, rqtn_ky from (
SELECT   (select max(dt_of_acpt) dt_of_acpt from rctg.whs_recr_act_fct raf, rctg.whs_recr_evnt_dim red
     where raf.rqtn_ky = whs.rqtn_ky
        and raf.recr_evnt_ky = red.recr_evnt_ky
        and red.recr_evnt_nm = 'Offer'
        and raf.recr_act_stat in ('Accepted','Hired-DO NOT USE')) as recr_act_stat_dt,
        whs.cand_ky, whs.rqtn_ky
  FROM   RCTG.WHS_RECR_EVNT_DIM WHS1
INNER JOIN RCTG.WHS_RECR_ACT_FCT WHS on whs1.recr_evnt_ky = whs.recr_evnt_ky
INNER JOIN RCTG.WHS_RQTN_DIM T1 on whs.rqtn_ky = t1.rqtn_ky
WHERE whs.cand_ky is not null
AND T1.RQTN_TTL_PSTN_CT = (SELECT count(*)
									 FROM RCTG.WHS_RECR_EVNT_DIM WHS5
									 inner join  RCTG.WHS_RECR_ACT_FCT WHS4 on whs5.recr_evnt_ky = whs4.recr_evnt_ky
									WHERE WHS4.RQTN_KY = WHS.RQTN_KY
									      AND  WHS5.RECR_EVNT_NM = 'Offer'
									      AND WHS4.RECR_ACT_STAT IN ('Accepted','Hired-DO NOT USE')
								)
AND  0 = (SELECT count(*)
           FROM RCTG.WHS_RECR_EVNT_DIM WHS3
           inner join  RCTG.WHS_RECR_ACT_FCT WHS2 on whs3.recr_evnt_ky = whs2.recr_evnt_ky
          WHERE WHS2.CAND_KY = WHS.CAND_KY
            AND WHS2.RQTN_KY = WHS.RQTN_KY
            AND ( (WHS3.RECR_EVNT_NM = 'Job Bid' AND WHS2.RECR_ACT_STAT = 'External - Remove From Consideration') OR
                  (WHS3.RECR_EVNT_NM = 'Screen' AND WHS2.RECR_ACT_STAT = 'No Interest') OR
                  (WHS3.RECR_EVNT_NM = 'Interview' AND WHS2.RECR_ACT_STAT = 'No Interest') OR
                  (WHS3.RECR_EVNT_NM = 'Test' AND WHS3.RECR_EVNT_TP = 'Drug Test' AND WHS2.RECR_ACT_STAT IN  ('Fail - Drug test','Drug check failed')) OR
                  (WHS3.RECR_EVNT_NM = 'Test' AND WHS3.RECR_EVNT_TP = 'Background Check' AND WHS2.RECR_ACT_STAT IN ('Fail - Background check','Fail -  Background check','Background check failed')) OR
                  (WHS3.RECR_EVNT_NM = 'Test' AND WHS2.RECR_ACT_STAT = 'Did Not Get Through Qualifier - QSP') OR
                  (WHS3.RECR_EVNT_NM = 'Offer' AND WHS2.RECR_ACT_STAT in ('Accepted','Hired-DO NOT USE') OR
                  (WHS2.RECR_ACT_STAT_RSN IS NOT NULL))
                )
        )

) t1 where date(recr_act_stat_dt) between vBeginDate and vEndDate

union

select recr_act_stat_dt, cand_ky, rqtn_ky 
from (
select date(evnt_dt) as recr_act_stat_dt, t1.unc_apl_id, t1.rqtn_nr, cand_ky, rqtn_ky
from stage_rctg.tbnt1 t1,
     rctg.whs_cand_dim t2,
     rctg.whs_rqtn_dim t3,
     stage_rctg.unc_applicant t4
where t1.unc_apl_id = t2.cand_id
  and t1.rqtn_nr = t3.rqtn_nr      
