DECLARE CUR_APP_NO_DISP1 CURSOR WITH RETURN FOR
SELECT  distinct date(t1.rqtn_curr_stat_dt), whs.cand_ky, whs.rqtn_ky
  FROM   RCTG.MQT_RECR_ACT_FCT WHS 
INNER JOIN RCTG.WHS_RQTN_DIM T1 on whs.rqtn_ky = t1.rqtn_ky
