RCTG.MQT_RECR_ACT_FCT t1
inner join rctg.whs_cand_dim t2 on t1.cand_ky = t2.cand_ky
inner join rctg.whs_rqtn_dim t5 on t1.rqtn_ky = t5.rqtn_ky
where t1.status_flag = 'D' 
and t1.job_bid_date > current date - 90 days
) t1
where rmvl_rsn1 not in ('080','AI','AG','I','200')
except all 
