/* Main */
DECLARE CONTINUE HANDLER FOR NOT FOUND SET V_NO_DATA = 1;--
/*call truncate('RCTG','WHS_tbnt_fct'); */
delete from RCTG.WHS_tbnt_fct;--
SET V_NO_DATA = 0;--
OPEN CUR_APP_NO_DISP;--
FETCH CUR_APP_NO_DISP INTO VAR_DT, VAR_CAND, VAR_RQTN;--
