SET V_NO_DATA = 0;--
OPEN CUR_APP_NO_DISP1;--
FETCH CUR_APP_NO_DISP1 INTO VAR_DT, VAR_CAND, VAR_RQTN;--
WHILE (V_NO_DATA = 0) DO
