SET V_NO_DATA = 0;--
OPEN CUR_FINAL_DISP;--
FETCH CUR_FINAL_DISP INTO VAR_DT, VAR_CAND, VAR_RQTN;--
WHILE (V_NO_DATA = 0) DO
  INSERT INTO RCTG.WHS_tbnt_fct VALUES( VAR_DT, VAR_CAND, VAR_RQTN);--
  FETCH CUR_FINAL_DISP INTO VAR_DT, VAR_CAND, VAR_RQTN;--
END WHILE;--
CLOSE CUR_FINAL_DISP;--
SET V_NO_DATA = 0;--
OPEN CUR_DISP_POST_OFR;--
FETCH CUR_DISP_POST_OFR INTO VAR_DT, VAR_CAND, VAR_RQTN;--
WHILE (V_NO_DATA = 0) DO
  INSERT INTO RCTG.WHS_tbnt_fct VALUES( VAR_DT, VAR_CAND, VAR_RQTN);--
  FETCH CUR_DISP_POST_OFR INTO VAR_DT, VAR_CAND, VAR_RQTN;--
END WHILE;--
CLOSE CUR_DISP_POST_OFR;--
SET V_NO_DATA = 0;--
OPEN CUR_UNICRU_DEL;--
FETCH CUR_UNICRU_DEL INTO VAR_DT, VAR_CAND, VAR_RQTN;--
WHILE (V_NO_DATA = 0) DO
  INSERT INTO RCTG.WHS_tbnt_fct VALUES( VAR_DT, VAR_CAND, VAR_RQTN);--
  FETCH CUR_UNICRU_DEL INTO VAR_DT, VAR_CAND, VAR_RQTN;--
 END WHILE;--
CLOSE CUR_UNICRU_DEL;--
/* Delete any rows where the tbnt_dt is not the min tbnt_dt */
delete from rctg.whs_tbnt_fct
where (tbnt_dt,cand_ky, rqtn_ky) in 
( select * from rctg.whs_tbnt_fct
except
select min(tbnt_dt) tbnt_dt, cand_ky, rqtn_ky
from rctg.whs_tbnt_fct
group by cand_ky, rqtn_ky
) ;--
commit;--
END;

