CREATE PROCEDURE RCTG_REPT.SP_TBNT
 (IN VBEGINDATE DATE, 
  IN VENDDATE DATE, 
  IN V_DATEPARM VARCHAR(3)
 ) 
  SPECIFIC RCTG_REPT.SQL070426100059700
  LANGUAGE SQL
  NOT DETERMINISTIC
  CALLED ON NULL INPUT
  MODIFIES SQL DATA
  INHERIT SPECIAL REGISTERS
  P1: BEGIN
    
    DECLARE cursor1 CURSOR WITH RETURN FOR
select distinct cand_ky, rqtn_ky, vBeginDate as begindate, vEndDate as enddate
 from (
SELECT   whs.recr_act_stat_dt, whs.cand_ky, whs.rqtn_ky
FROM     RCTG_REPT.WHS_RECR_ACT_FCT WHS, 
         RCTG_REPT.WHS_RECR_EVNT_DIM WHS1,
         RCTG_REPT.WHS_RQTN_DIM T1
WHERE    WHS.RECR_EVNT_KY=WHS1.RECR_EVNT_KY 
AND      DATE(WHS.RECR_ACT_STAT_DT) between vBeginDate and vEndDate
AND      ( (WHS1.RECR_EVNT_NM = 'Job Bid' AND WHS.RECR_ACT_STAT = 'External - Remove From Consideration') OR
           (WHS1.RECR_EVNT_NM = 'Screen' AND WHS.RECR_ACT_STAT = 'No Interest') OR
           (WHS1.RECR_EVNT_NM = 'Interview' AND WHS.RECR_ACT_STAT = 'No Interest') OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS1.RECR_EVNT_TP = 'Drug Test' AND WHS.RECR_ACT_STAT in  ('Fail - Drug test','Drug check failed')) OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS1.RECR_EVNT_TP = 'Background Check' AND WHS.RECR_ACT_STAT in ('Fail - Background check','Fail -  Background check','Background check failed')) OR
           (WHS1.RECR_EVNT_NM = 'Test' AND WHS.RECR_ACT_STAT = 'Did Not Get Through Qualifier - QSP')
          )
AND WHS.SRC_SYS_ID = 'D'
AND WHS.RQTN_KY = T1.RQTN_KY
AND T1.RQTN_CURR_STAT in ('Open','Hold','ReOpen','Offer','Canceled')
AND 0 < (select count(*) from RCTG_REPT.whs_recr_act_fct t10, RCTG_REPT.whs_recr_evnt_dim t11
          where t10.recr_evnt_ky = t11.recr_evnt_ky
            and t11.recr_evnt_nm = 'Job Bid'
            and t11.recr_evnt_tp = 'External'
            and t10.cand_ky = whs.cand_ky
            and t10.rqtn_ky = whs.rqtn_ky)
            
union

