CREATE PROCEDURE RIDQADM.EFCV_DT_PROC
 (IN BGDT_TABLE VARCHAR(40), 
  IN ENDT_TABLE VARCHAR(40), 
  IN EFCV_DT_TABLE VARCHAR(40)
 ) 
  SPECIFIC RIDQADM.EFCV_DT_PROC
  LANGUAGE SQL
  NOT DETERMINISTIC
  CALLED ON NULL INPUT
  MODIFIES SQL DATA
  INHERIT SPECIAL REGISTERS
  -------------------------------------------------------
-- PROCEDURE TITLE: EFCV_DT_PROC_MOD			 	 --
--							  				  		 --
-- DATE CREATED: JUNE 22, 2005				  		 --
-- 				 	 						  		 --
-- CREATED BY: SIRISHA GORANTLA			
-- MODIFIED BY : KUNAL
-------------------------------------------------------
-- PROCEDURE DESCRIPTION: THIS PROCEDURE CALCULATES  --
-- THE  POSSIBLE EFFECTIVE DATE RANGES FOR GIVEN     --
-- BEGIN AND END DATES 	   			   	   			 --
-- 	  			 							  		 --
-- TABLES USED: BEGIN DATE TABLE,END DATE TABLE,	 --
-- EFFECTIVE DATE TABLE						  		 --
-- 			 	  									 --
-- NOTE:REFER TO Effective dates strategy.doc 		 --
-- DOCUMENT FOR LOGIC TO CALCULATE EFFECTIVE		 --
-- DATES FOR DIFFERENT BEGIN AND END DATES			 --
-------------------------------------------------------
-- DATE MODIFIED:  07/20/2005    			  		 --
-- MODIFIED BY:   SIRISHA GORANTLA					 --
-- MODIFICATION DESCRIPTION: ADDED LOGIC TO CALCULATE--
-- EFFECTIVE DATES FOR TABLES WITH SAME BEGIN DATES --
-- BUT DIFFERENT END DATES 				  		     --
-------------------------------------------------------


-- BEGIN PROCEDURE 
BEGIN

-- DECLARE DYNAMIC SQL STATEMENT VARIABLES,SQLCODES AND SQLSTATES
DECLARE CURR_BGDT_STMT varCHAR(1000);--
DECLARE CURR_ENDT_STMT varCHAR(1000);--
DECLARE NEXT_BGDT_STMT varCHAR(1000);--
DECLARE CURR_SOURCE_ID_STMT VARCHAR(1000);--
DECLARE MAX_BGDT_STMT VARCHAR(500);--
DECLARE MAX_ENDT_STMT VARCHAR(500);--
DECLARE DELETE_EFCV_DT_STMT VARCHAR(500);--
DECLARE SQLSTATE CHAR(5) default '00000';--
DECLARE TURN_OFF_LOG_STMT VARCHAR(500);--

-- DECLARE DATE VARIABLES 
DECLARE CURR_BGDT DATE;--
DECLARE NEXT_BGDT DATE;--
DECLARE CURR_ENDT DATE;--
DECLARE MAX_BGDT DATE;--
DECLARE MAX_ENDT DATE;--
DECLARE EFCV_BGDT DATE;--
DECLARE EFCV_ENDT DATE;--
   -- KV: Added variable to handle same day changes
DECLARE TEMP_CURR_ENDT DATE;--
   -- KV: Ends   

-- DECLARE PRIMARY ID VARIABLE AND LOOP VARIABLES
DECLARE SOURCE_ID VARCHAR(200)  ; --
DECLARE CLNT_INTN_ID INTEGER;--
DECLARE END_OF_BGDT SMALLINT DEFAULT 0;--
DECLARE END_LOOP SMALLINT DEFAULT 0;--
DECLARE INCR_FLAG SMALLINT DEFAULT 0;--

-- DECLARE CURSORS
DECLARE INSERT_SQL VARCHAR(1000);--
DECLARE C_CURR_BGDT CURSOR FOR S1;--
DECLARE C_CURR_ENDT CURSOR FOR S3;--
DECLARE C_NEXT_BGDT CURSOR FOR S2;--
DECLARE C_MAX_BGDT CURSOR FOR S4;--
DECLARE C_SOURCE_ID CURSOR FOR S5;--
DECLARE C_MAX_ENDT CURSOR FOR S8;--



-- PREPARE STATEMENTS FOR DYNAMIC SQLs

SET CURR_BGDT_STMT='SELECT BGDT FROM ' || BGDT_TABLE||' WHERE SOURCE_ID =?'||' AND CLNT_INTN_ID=? ' ||' ORDER BY BGDT';--
PREPARE S1 FROM CURR_BGDT_STMT; --

SET NEXT_BGDT_STMT='SELECT MIN(BGDT) FROM '|| BGDT_TABLE|| ' WHERE BGDT > ? '||' AND SOURCE_ID= ?'||' AND CLNT_INTN_ID=? '  ;--
PREPARE S2 FROM NEXT_BGDT_STMT; --

SET CURR_ENDT_STMT='SELECT ENDT FROM '|| ENDT_TABLE ||' WHERE SOURCE_ID=?'||' AND CLNT_INTN_ID=? ' ||' ORDER BY ENDT';--
PREPARE S3 FROM CURR_ENDT_STMT;--

SET CURR_SOURCE_ID_STMT='SELECT DISTINCT SOURCE_ID,CLNT_INTN_ID  FROM '|| BGDT_TABLE ;--||' WHERE SOURCE_ID LIKE '||''''||'201500015%'||''''; --||' OR SOURCE_ID LIKE '||''''||'0004%'||'''';--
PREPARE S5 FROM CURR_SOURCE_ID_STMT;--

SET MAX_BGDT_STMT='SELECT MAX(BGDT) FROM '||BGDT_TABLE||'  WHERE SOURCE_ID =?' ||' AND CLNT_INTN_ID=? ' ;--
PREPARE S4 FROM MAX_BGDT_STMT;--

SET MAX_ENDT_STMT='SELECT MAX(ENDT) FROM '||ENDT_TABLE||'  WHERE SOURCE_ID =?' ||' AND CLNT_INTN_ID=? ' ;--
PREPARE S8 FROM MAX_ENDT_STMT;--

-- DELETES THE EFFECTIVE DATE TABLE BEFORE LOADING
SET DELETE_EFCV_DT_STMT='DELETE FROM '|| EFCV_DT_TABLE;--
PREPARE S7 FROM DELETE_EFCV_DT_STMT;--
EXECUTE S7;--

SET TURN_OFF_LOG_STMT = 'alter table ' || EFCV_DT_TABLE || ' activate not logged initially';--
EXECUTE IMMEDIATE TURN_OFF_LOG_STMT;--

--OPEN AND FETCH SOURCE_ID
OPEN C_SOURCE_ID;--
FETCH C_SOURCE_ID INTO SOURCE_ID,CLNT_INTN_ID;--
-- LOOP THRO THE LOGIC FOR ALL THE SOURCE_IDs
WHILE(SQLSTATE<>'02000') DO
        --OPEN DATE CURSORS FOR EACH SOURCE_ID 
        OPEN C_CURR_BGDT USING SOURCE_ID,CLNT_INTN_ID;--
        OPEN C_CURR_ENDT USING SOURCE_ID,CLNT_INTN_ID;--
        OPEN C_MAX_BGDT USING SOURCE_ID,CLNT_INTN_ID;--
        OPEN C_MAX_ENDT USING SOURCE_ID,CLNT_INTN_ID;--
				
        -- FETCH DATES INTO DEFINED VARIABLES
        FETCH C_CURR_ENDT INTO CURR_ENDT ;--
        FETCH C_CURR_BGDT INTO CURR_BGDT ;--
        FETCH C_MAX_BGDT INTO MAX_BGDT;--
        FETCH C_MAX_ENDT INTO MAX_ENDT;--
			  
        -- OPEN AND FETCH THE NEXT BEGIN DATE ONLY WHEN THE CURRENT BEGIN DATE 
        -- IS NOT AT THE LAST RECORD  
        IF(MAX_BGDT > CURR_BGDT) THEN	
                OPEN C_NEXT_BGDT USING CURR_BGDT,SOURCE_ID,CLNT_INTN_ID;	   --
                FETCH C_NEXT_BGDT INTO NEXT_BGDT ;--
                CLOSE C_NEXT_BGDT;--
        END IF;--
	
        --LOOP THRO BEGIN DATES 				
        WHILE(END_OF_BGDT<>1) DO
                -- LOOP WHEN THE CURRENT BEGIN DATE IS SAME AS THE CURRENT END DATE
                WHILE(CURR_BGDT=CURR_ENDT AND END_LOOP=0 ) DO
                        SET EFCV_BGDT = CURR_BGDT ;--
                        SET EFCV_ENDT = CURR_ENDT;--
                        --insert into test_proc1 values (source_id,curr_bgdt , curr_endt,next_bgdt,'equal loop');--
                        SET INSERT_SQL='INSERT INTO '|| EFCV_DT_TABLE || '(SOURCE_ID,CLNT_INTN_ID,BGDT,ENDT) VALUES(?,?,?,?)';--
                        PREPARE S6 FROM INSERT_SQL;--
                        EXECUTE S6 USING SOURCE_ID,CLNT_INTN_ID,EFCV_BGDT,EFCV_ENDT;--
						
                        -- QUIT THE LOOP WHEN THE CURRENT END DATE IS THE LAST RECORD
                        IF (CURR_ENDT = MAX_ENDT) THEN 
                                SET END_OF_BGDT=1;--
                        END IF;--
						
                        -- FETCH THE NEXT CURRENT BEGIN DATE RECORD AND THE NEXT NEXT BEGIN DATE RECORD
                        IF(CURR_ENDT=NEXT_BGDT - 1 DAY AND CURR_BGDT<>MAX_BGDT) THEN
                                FETCH C_CURR_BGDT INTO CURR_BGDT ;--
                                IF(MAX_BGDT > NEXT_BGDT) THEN	
                                        OPEN C_NEXT_BGDT USING CURR_BGDT,SOURCE_ID,CLNT_INTN_ID;	   --
                                        FETCH C_NEXT_BGDT INTO NEXT_BGDT ;--
                                        CLOSE C_NEXT_BGDT;--
                                END IF;--
			ELSEIF(CURR_ENDT<>MAX_ENDT AND (NEXT_BGDT <> CURR_ENDT + 1 DAY OR  CURR_BGDT>=MAX_BGDT)) THEN
                                SET CURR_BGDT=CURR_ENDT + 1 DAY;--
                        END IF;--
						
                        -- FETCH THE NEXT END DATE RECORD
                        IF (MAX_ENDT > CURR_ENDT) THEN   
        		        FETCH C_CURR_ENDT INTO CURR_ENDT;--
			ELSE
                                SET END_LOOP=1; -- END LOOP WHEN CURRENT END DATE IS THE LAST END DATE TO PROCESS
			END IF;--
		END WHILE;--
					
		SET END_LOOP=0;--

                -- FETCH END DATE FROM NEXT BEGIN DATE IF NEXT BEGIN DATE IS EARLIER THAN CURRENT END DATE
                IF (NEXT_BGDT < CURR_ENDT AND CURR_BGDT<MAX_BGDT AND END_OF_BGDT<>1) THEN
                        SET EFCV_BGDT = CURR_BGDT ;--
                        SET EFCV_ENDT = NEXT_BGDT - 1 DAY;--
                        --insert into test_proc1 values (source_id,curr_bgdt , curr_endt,next_bgdt,'first if');--
                        SET INSERT_SQL='INSERT INTO '|| EFCV_DT_TABLE || '(SOURCE_ID,CLNT_INTN_ID,BGDT,ENDT) VALUES(?,?,?,?)';--
                        PREPARE S6 FROM INSERT_SQL;--
                        EXECUTE S6 USING SOURCE_ID,CLNT_INTN_ID,EFCV_BGDT,EFCV_ENDT;--
                        
                        IF(CURR_BGDT<MAX_BGDT) THEN
                                FETCH C_CURR_BGDT INTO CURR_BGDT ;--
                        ELSE
                                SET END_OF_BGDT=1;--
                        END IF;--
						
                        IF(MAX_BGDT > NEXT_BGDT) THEN	
                                OPEN C_NEXT_BGDT USING CURR_BGDT,SOURCE_ID,CLNT_INTN_ID;	   --
                                FETCH C_NEXT_BGDT INTO NEXT_BGDT ;--
                                CLOSE C_NEXT_BGDT;--
                        END IF;--
                -- FETCH END DATE FROM CURRENT END DATE IF IT COMES EARLIER THAN NEXT BEGIN DATE
                ELSEIF((NEXT_BGDT >= CURR_ENDT OR CURR_BGDT>=MAX_BGDT) AND END_OF_BGDT<>1 ) THEN
        	 	SET EFCV_BGDT = CURR_BGDT;--
        	 	
        	 	-- KV: Added condition to handle same bgdt and endt
        		IF ( NEXT_BGDT = CURR_ENDT ) THEN
        		        SET EFCV_ENDT = CURR_ENDT - 1 DAY;--
                        ELSE
                                SET EFCV_ENDT = CURR_ENDT;							--
			END IF;--
			-- KV: Change ends
			
                        --insert into test_proc1 values (source_id,curr_bgdt , curr_endt,next_bgdt,'second if');--
                        SET INSERT_SQL='INSERT INTO '|| EFCV_DT_TABLE || '(SOURCE_ID,CLNT_INTN_ID,BGDT,ENDT) VALUES(?,?,?,?)';--
                        PREPARE S6 FROM INSERT_SQL;--
                        EXECUTE S6 USING SOURCE_ID,CLNT_INTN_ID,EFCV_BGDT,EFCV_ENDT;--
                        IF (CURR_ENDT = MAX_ENDT) THEN 
                                SET END_OF_BGDT=1;--
		        END IF;--
								
                        IF((NEXT_BGDT=CURR_ENDT + 1 DAY OR NEXT_BGDT=CURR_ENDT) AND (CURR_BGDT <> MAX_BGDT OR CURR_ENDT <> MAX_ENDT)) THEN	
                                FETCH C_CURR_BGDT INTO CURR_BGDT ;--
			ELSEIF(CURR_ENDT<>MAX_ENDT AND (NEXT_BGDT <> CURR_ENDT + 1 DAY OR  CURR_BGDT>=MAX_BGDT)) THEN
                                SET CURR_BGDT=CURR_ENDT + 1 DAY;--
			END IF;--

                        -- KV: Added change to handle same bgdt and endt
                        SET TEMP_CURR_ENDT = CURR_ENDT;--
                        IF(MAX_ENDT > CURR_ENDT AND (NEXT_BGDT <> CURR_ENDT OR NEXT_BGDT IS NULL)) THEN
                                FETCH C_CURR_ENDT INTO CURR_ENDT;--
                        END IF;--
                        -- KV: Change ends
			-- KV: In the following if condition, changed CURR_ENDT to TEMP_CURR_ENDT as CURR_ENDT is modified above.					
                        IF(MAX_BGDT > NEXT_BGDT AND (NEXT_BGDT=TEMP_CURR_ENDT + 1 DAY or NEXT_BGDT=TEMP_CURR_ENDT)) THEN	
                                OPEN C_NEXT_BGDT USING CURR_BGDT,SOURCE_ID,CLNT_INTN_ID;	   --
                                FETCH C_NEXT_BGDT INTO NEXT_BGDT ;--
                                CLOSE C_NEXT_BGDT;--
                        END IF;--
                        
                        -- KV: Commented below condition for same bgdt and endt. This condition is moved up.				   				
                --	IF(MAX_ENDT > CURR_ENDT) THEN
                --	   FETCH C_CURR_ENDT INTO CURR_ENDT;--
                --	END IF;--
                        -- KV: Comment ends                        
                END IF;--
        END WHILE; --
        -- CLOSE ALL CURSORS
        CLOSE C_CURR_BGDT;--
        CLOSE C_CURR_ENDT;	--
        CLOSE C_MAX_BGDT;--
        CLOSE C_MAX_ENDT;--
        SET END_OF_BGDT=0;--
        SET NEXT_BGDT = NULL;--
        -- FETCH THE NEXT SOURCE ID RECORD
        FETCH C_SOURCE_ID INTO SOURCE_ID,CLNT_INTN_ID;--
END WHILE; --
CLOSE C_SOURCE_ID; --
END ;

