CREATE PROCEDURE RIDQADM.SP_SET_DELTA
 (IN P_RUN_TYPE INTEGER
 ) 
  SPECIFIC RIDQADM.SP_SET_DELTA
  LANGUAGE SQL
  NOT DETERMINISTIC
  CALLED ON NULL INPUT
  MODIFIES SQL DATA
  INHERIT SPECIAL REGISTERS
  -- BEGIN PROCEDURE 
BEGIN

-- DECLARE DYNAMIC SQL STATEMENT VARIABLES;SQLCODES AND SQLSTATES
DECLARE SQLSTATE CHAR(5) default '00000';--
DECLARE V_START_TS    TIMESTAMP;--
DECLARE V_STOP_TS   TIMESTAMP;--
DECLARE V_NEW_START_TS  TIMESTAMP;--
DECLARE V_NEW_STOP_TS  TIMESTAMP;--
DECLARE V_COUNT    INTEGER;--

SELECT COUNT(*) INTO V_COUNT FROM RIDQADM.WHS_LOAD_CYC;--

SET V_NEW_STOP_TS = CAST(SUBSTR(CAST(CURRENT_TIMESTAMP + 1 SECOND AS CHAR(26)),1,20) || '000000' AS TIMESTAMP);--

-- SETTING THE P_RUN_TYPE VARIABLE TO 0 TELLS THE PROC TO SET THE START DATE TO A LOW DATE.  THIS WILL
-- ALSO HAPPEN IF THERE IS NO DATA IN THE WHS_LOAD_CYC TABLE.

IF P_RUN_TYPE = 0 OR V_COUNT = 0 THEN
   INSERT INTO RIDQADM.WHS_LOAD_CYC (START_TS, STOP_TS) VALUES ('1900-01-01-00.00.00.000000', V_NEW_STOP_TS);--
ELSE
   SELECT START_TS, STOP_TS INTO V_START_TS, V_STOP_TS FROM RIDQADM.WHS_LOAD_CYC WHERE LOAD_CYC IN (SELECT MAX(LOAD_CYC) FROM RIDQADM.WHS_LOAD_CYC);--
   SET V_NEW_START_TS = CAST(SUBSTR(CAST(V_STOP_TS + 1 SECOND AS CHAR(26)),1,20) || '000000' AS TIMESTAMP);--
   INSERT INTO RIDQADM.WHS_LOAD_CYC (START_TS, STOP_TS) VALUES (V_NEW_START_TS, V_NEW_STOP_TS);--
END IF;--

COMMIT;--

END 
 ;

