CREATE PROCEDURE RIDQADM.ADD_AUX_USERS
(IN P_USER VARCHAR(40),    IN P_ROLE VARCHAR(40)  )
SPECIFIC RIDQADM.ADD_AUX_USERS
LANGUAGE SQL
NOT DETERMINISTIC
CALLED ON NULL INPUT
MODIFIES SQL DATA
INHERIT SPECIAL REGISTERS

BEGIN
DECLARE SQLSTATE            CHAR(5) default '00000';--
DECLARE V_USER_ID                   VARCHAR(50);--
DECLARE V_ROLE_CD                   VARCHAR(30);--
DECLARE V_CNT                   INTEGER;--
DECLARE V_MGR_ID                    VARCHAR(50);--
DECLARE V_CNT_MGR           INTEGER;--
DECLARE C_AUX CURSOR FOR SELECT DISTINCT EE_ID,ROLE_CD FROM RIDQADM.STG_AUX_USER WHERE ROLE_CD NOT IN ('MANAGER');--
DECLARE C_AUX_MGR CURSOR FOR SELECT DISTINCT EE_ID FROM RIDQADM.STG_AUX_USER WHERE ROLE_CD IN ('MANAGER');--

SET V_CNT = 1;--
SET V_CNT_MGR = 1;--

OPEN C_AUX;--
FETCH FROM C_AUX INTO V_USER_ID, V_ROLE_CD;--

WHILE (SQLSTATE <> '02000' ) DO
SET V_CNT = V_CNT +1;--
CALL RIDQADM.ADD_USER_ROLE(V_USER_ID,V_ROLE_CD);--
FETCH FROM C_AUX INTO V_USER_ID,V_ROLE_CD ;--
END WHILE;--

CLOSE C_AUX;--

OPEN C_AUX_MGR;--
FETCH FROM C_AUX_MGR INTO V_MGR_ID;--

WHILE (SQLSTATE <> '02000' ) DO
SET V_CNT_MGR = V_CNT_MGR +1;--
CALL RIDQADM.ADD_MGR_ROLE(V_MGR_ID);--
FETCH FROM C_AUX_MGR INTO V_MGR_ID;--
END WHILE;--

CLOSE C_AUX_MGR;--

END ;

