declare varint1 integer default 0;--
declare var1 varchar(100);--
set var1='DROP INDEX '  ||PM1 || '.' || PM2;--
PREPARE s1 FROM var1;--
 SELECT count(*) into varint1  FROM sysibm.sysindexes WHERE  CREATOR=PM1 AND name = PM2 ;--
IF varint1 > 0
THEN                                                                      
 EXECUTE s1;                                               --
END IF ;      --
END;

